): Promise<void> => {
  try {
    // Verifica se l'utente è già membro
    const existingMemberQuery = query(
      collection(db, 'list_members'),
      where('listId', '==', listId),
      where('userId', '==', userId)
    );
    const existingMembers = await getDocs(existingMemberQuery);

    if (!existingMembers.empty) {
      throw new Error('User is already a member of this list');
    }

    const memberData: Omit<ListMember, 'id'> = {
      listId,
      userId,
      role,
      joinedAt: new Date()
    };

    await addDoc(collection(db, 'list_members'), memberData);
  } catch (error) {
    console.error("Error adding member to list:", error);
    throw error;
  }
};

export const removeMemberFromList = async (
  listId: string,
  userId: string
): Promise<void> => {
  try {
    const memberQuery = query(
      collection(db, 'list_members'),
      where('listId', '==', listId),
      where('userId', '==', userId)
    );
    const memberSnapshot = await getDocs(memberQuery);

    if (memberSnapshot.empty) {
      throw new Error('Member not found');
    }

    // Non permettere di rimuovere l'owner
    const memberData = memberSnapshot.docs[0].data() as ListMember;
    if (memberData.role === 'owner') {
      throw new Error('Cannot remove owner from list');
    }

    await deleteDoc(memberSnapshot.docs[0].ref);
  } catch (error) {
    console.error("Error removing member from list:", error);
    throw error;
  }
};

// --- Utilità ---

export const checkGemInList = async (listId: string, gemId: string): Promise<boolean> => {
  try {
    const itemQuery = query(
      collection(db, 'list_items'),
      where('listId', '==', listId),
      where('gemId', '==', gemId)
    );
    const itemSnapshot = await getDocs(itemQuery);
    return !itemSnapshot.empty;
  } catch (error) {
    console.error("Error checking gem in list:", error);
    return false;
  }
};

// --- Migrazione (per convertire le liste esistenti) ---

export const migrateUserLists = async (userId: string, oldLists: { id: string; name: string; gemIds: string[] }[]): Promise<void> => {
  try {
    const batch = writeBatch(db);

    for (const oldList of oldLists) {
      // Crea la nuova lista
      const listRef = doc(collection(db, 'lists'));
      const listData: Omit<List, 'id'> = {
        name: oldList.name,
        isPublic: false,
        createdBy: userId,
        createdAt: new Date(),
        updatedAt: new Date()
      };

      batch.set(listRef, listData);

      // Aggiungi il creatore come owner
      const memberRef = doc(collection(db, 'list_members'));
      const memberData: Omit<ListMember, 'id'> = {
        listId: listRef.id,
        userId,
        role: 'owner',
        joinedAt: new Date()
      };

      batch.set(memberRef, memberData);

      // Aggiungi tutti i gems
      for (const gemId of oldList.gemIds) {
        const itemRef = doc(collection(db, 'list_items'));
        const itemData: Omit<ListItem, 'id'> = {
          listId: listRef.id,
          gemId,
          addedBy: userId,
          addedAt: new Date()
        };

        batch.set(itemRef, itemData);
      }
    }

    await batch.commit();
  } catch (error) {
    console.error("Error migrating user lists:", error);
    throw error;
  }
};

import { UserRole, UserPermissions } from '../types';

/**
 * Restituisce i permessi di default per ogni ruolo
 */
export const getDefaultPermissions = (role: UserRole): UserPermissions => {
  switch (role) {
    case UserRole.ADMIN:
      return {
        canCreateGems: true,
        canEditGems: true,
        canDeleteGems: true,
        canManageUsers: true,
        canModerateContent: true,
      };

    case UserRole.MODERATOR:
      return {
        canCreateGems: true,
        canEditGems: true,
        canDeleteGems: false,
        canManageUsers: false,
        canModerateContent: true,
      };

    case UserRole.USER:
    default:
      return {
        canCreateGems: false,
        canEditGems: false,
        canDeleteGems: false,
        canManageUsers: false,
        canModerateContent: false,
      };
  }
};

/**
 * Verifica se un utente ha un permesso specifico
 */
export const hasPermission = (
  userPermissions: UserPermissions,
  permission: keyof UserPermissions
): boolean => {
  return userPermissions[permission] === true;
};

/**
 * Verifica se un utente ha almeno uno dei ruoli specificati
 */
export const hasRole = (userRole: UserRole, allowedRoles: UserRole[]): boolean => {
  return allowedRoles.includes(userRole);
};

/**
 * Verifica se un utente è admin
 */
export const isAdmin = (userRole: UserRole): boolean => {
  return userRole === UserRole.ADMIN;
};

/**
 * Verifica se un utente è moderatore o admin
 */
export const isModerator = (userRole: UserRole): boolean => {
  return userRole === UserRole.MODERATOR || userRole === UserRole.ADMIN;
};

/**
 * Hook personalizzato per verificare i permessi dell'utente corrente
 */
export const useUserPermissions = (user: { role: UserRole; permissions: UserPermissions } | null) => {
  if (!user) {
    return {
      canCreateGems: false,
      canEditGems: false,
      canDeleteGems: false,
      canManageUsers: false,
      canModerateContent: false,
      isAdmin: false,
      isModerator: false,
    };
  }

  return {
    ...user.permissions,
    isAdmin: isAdmin(user.role),
    isModerator: isModerator(user.role),
  };
};

