option.value:
rules_version = '2';
service cloud.firestore {
  match /databases/{database}/documents {
    // Regole per gli utenti
    match /users/{userId} {
      allow read, write: if request.auth != null && request.auth.uid == userId;
      // Permetti agli admin di leggere tutti gli utenti
      allow read: if request.auth != null && 
        exists(/databases/$(database)/documents/users/$(request.auth.uid)) &&
        get(/databases/$(database)/documents/users/$(request.auth.uid)).data.role == 'admin';
    }

    // Regole per le gemme (lettura pubblica, scrittura per admin)
    match /gems/{gemId} {
      allow read: if true; // Lettura pubblica
      allow write: if request.auth != null && 
        exists(/databases/$(database)/documents/users/$(request.auth.uid)) &&
        get(/databases/$(database)/documents/users/$(request.auth.uid)).data.permissions.canCreateGems == true;

      // Domande degli utenti sulle gemme
      match /userQuestions/{questionId} {
        allow read: if true;
        allow write: if request.auth != null;
      }
    }

    // Regole per i canali (lettura pubblica)
    match /channels/{channelId} {
      allow read: if true;
      allow write: if request.auth != null && 
        exists(/databases/$(database)/documents/users/$(request.auth.uid)) &&
        get(/databases/$(database)/documents/users/$(request.auth.uid)).data.role == 'admin';
    }

    // Regole per i suggerimenti di argomenti
    match /topicSuggestions/{suggestionId} {
      allow read, write: if request.auth != null;
    }

    // ===== REGOLE PER LE NUOVE LISTE =====

    // Regole per le liste
    match /lists/{listId} {
      allow read, write: if request.auth != null && 
        resource.data.createdBy == request.auth.uid;
      allow create: if request.auth != null;
      allow read: if request.auth != null && resource.data.isPublic == true;
    }

    // Regole per i membri delle liste
    match /list_members/{memberId} {
      allow read, write: if request.auth != null && 
        resource.data.userId == request.auth.uid;
      allow read, write: if request.auth != null && 
        exists(/databases/$(database)/documents/lists/$(resource.data.listId)) &&
        get(/databases/$(database)/documents/lists/$(resource.data.listId)).data.createdBy == request.auth.uid;
    }

    // Regole per gli elementi delle liste
    match /list_items/{itemId} {
      allow read, write: if request.auth != null && 
        exists(/databases/$(database)/documents/lists/$(resource.data.listId)) &&
        get(/databases/$(database)/documents/lists/$(resource.data.listId)).data.createdBy == request.auth.uid;
      allow read: if request.auth != null && 
        exists(/databases/$(database)/documents/lists/$(resource.data.listId)) &&
        get(/databases/$(database)/documents/lists/$(resource.data.listId)).data.isPublic == true;
    }
  }
}

{
  "name": "curiow",
  "private": true,
  "version": "0.0.0",
  "type": "module",
  "scripts": {
    "dev": "vite",
    "build": "vite build",
    "preview": "vite preview",
    "seed": "node --loader ts-node/esm seed.ts"
  },
  "dependencies": {
    "@google/genai": "^1.13.0",
    "firebase": "^12.1.0",
    "react": "^19.1.1",
    "react-dom": "^19.1.1"
  },
  "devDependencies": {
    "@types/node": "^22.14.0",
    "ts-node": "^10.9.2",
    "typescript": "~5.8.2",
    "vite": "^6.2.0"
  }
}

import React, { useState, useEffect, useMemo } from 'react';
import { Gem, User, SavedList, Channel, Filter, Topic, ListWithItems, UserRole } from './types';
import { auth, googleProvider } from './services/firebase';
import { onAuthStateChanged, User as FirebaseUser, createUserWithEmailAndPassword, signInWithEmailAndPassword, signOut, signInWithPopup } from 'firebase/auth';
import * as firestoreService from './services/firestoreService';
import * as feedbackService from './services/feedbackService';
import Header from './components/Header';
import GemCard from './components/GemCard';
import GemDetailView from './components/GemDetailView';
import LoginModal from './components/LoginModal';
import SavedView from './components/SavedView';
import ProfileView from './components/ProfileView';
import AdminDashboard from './components/admin/AdminDashboard';
import TopicManagement from './components/admin/TopicManagement';
import FeedbackButton from './components/FeedbackButton';
import FeedbackModal from './components/FeedbackModal';
import { SparklesIcon } from './components/icons';
import SaveToListModal from './components/SaveToListModal';
// Import admin utils in development
import './utils/adminUtils';

type View = 'feed' | 'detail' | 'saved' | 'profile' | 'dashboard' | 'topics';

const App: React.FC = () => {
  const [gems, setGems] = useState<Gem[]>([]);
  const [channels, setChannels] = useState<Channel[]>([]);
  const [isLoading, setIsLoading] = useState(true);

  const [firebaseUser, setFirebaseUser] = useState<FirebaseUser | null>(null);
  const [user, setUser] = useState<User | null>(null);
  const [userLists, setUserLists] = useState<ListWithItems[]>([]);
  const [isMigrated, setIsMigrated] = useState(false);

  const [filter, setFilter] = useState<Filter>({ type: 'all' });
  const [showLoginModal, setShowLoginModal] = useState(false);
  const [currentView, setCurrentView] = useState<View>('feed');
  const [selectedGemId, setSelectedGemId] = useState<string | null>(null);

  const [isSaveModalOpen, setIsSaveModalOpen] = useState(false);
  const [gemToSaveId, setGemToSaveId] = useState<string | null>(null);

  // Stati per il sistema di feedback
  const [showFeedbackModal, setShowFeedbackModal] = useState(false);
  const feedbackButtonRef = React.useRef<any>(null);

  // Fetch initial static-like data
  useEffect(() => {
    const fetchInitialData = async () => {
        setIsLoading(true);
        const [fetchedGems, fetchedChannels] = await Promise.all([
            firestoreService.fetchGems(),
            firestoreService.fetchChannels(),
        ]);
        setGems(fetchedGems);
        setChannels(fetchedChannels);
        setIsLoading(false);
    };
    fetchInitialData();
  }, []);

  // Auth state listener
  useEffect(() => {
      const unsubscribe = onAuthStateChanged(auth, async (currentUser) => {
          console.log('Auth state changed - currentUser:', currentUser?.uid);
          setFirebaseUser(currentUser);
          if (currentUser) {
              let userProfile = await firestoreService.fetchUserProfile(currentUser.uid);
              console.log('Fetched user profile:', userProfile);

              // Se il profilo non esiste, creane uno nuovo. Utile per utenti già esistenti in Auth ma non in Firestore.
              if (!userProfile) {
                  console.log(`Creating new profile for user ${currentUser.uid}`);
                  const email = currentUser.email || 'no-email@example.com';
                  const [firstName, lastName] = email.split('@')[0].split('.') || [email, ''];
                  await firestoreService.createUserProfile(currentUser.uid, email, firstName, lastName || '');
                  userProfile = await firestoreService.fetchUserProfile(currentUser.uid);
                  console.log('Created new user profile:', userProfile);
              }

              // Migrazione automatica alle nuove liste
              console.log('Attempting migration to new list structure...');
              const migrationSuccess = await firestoreService.migrateUserToNewListStructure(currentUser.uid);
              setIsMigrated(migrationSuccess);

              // Carica le liste con la nuova struttura
              const newUserLists = await firestoreService.fetchUserListsNew(currentUser.uid);
              setUser(userProfile);
              setUserLists(newUserLists);
              setShowLoginModal(false);

              console.log('Migration completed:', migrationSuccess);
              console.log('Loaded user lists:', newUserLists);
          } else {
              // User is signed out
              setUser(null);
              setUserLists([]);
              setIsMigrated(false);
              setFilter({ type: 'all' });
              setCurrentView('feed');
          }
      });
      return () => unsubscribe();
  }, []);

  // Listener for user questions on selected gem
  useEffect(() => {
    if (!selectedGemId) return;

    const unsubscribe = firestoreService.listenToUserQuestions(selectedGemId, (questions) => {
        setGems(prevGems => prevGems.map(gem => 
            gem.id === selectedGemId ? { ...gem, userQuestions: questions } : gem
        ));
    });

    return () => unsubscribe();
  }, [selectedGemId]);

  const handleSignUpAttempt = async (email: string, pass: string, firstName: string, lastName: string) => {
      const userCredential = await createUserWithEmailAndPassword(auth, email, pass);
      await firestoreService.createUserProfile(userCredential.user.uid, email, firstName, lastName);
  };

  const handleLoginAttempt = async (email: string, pass: string) => {
      await signInWithEmailAndPassword(auth, email, pass);
  };

  const handleGoogleAuth = async () => {
    try {
      const result = await signInWithPopup(auth, googleProvider);
      const user = result.user;

      // Controlla se il profilo utente esiste già
      let userProfile = await firestoreService.fetchUserProfile(user.uid);

      if (!userProfile) {
        // Se è la prima volta che l'utente accede con Google, crea il profilo
        const email = user.email || 'no-email@example.com';
        const firstName = user.displayName?.split(' ')[0] || 'Nome';
        const lastName = user.displayName?.split(' ').slice(1).join(' ') || 'Cognome';

        await firestoreService.createUserProfile(user.uid, email, firstName, lastName);
      }

      // Il resto della logica di login è gestita automaticamente dall'listener onAuthStateChanged
    } catch (error: any) {
      console.error('Errore nell\'autenticazione Google:', error);
      throw new Error('Errore nell\'autenticazione con Google: ' + error.message);
    }
  };

  const handleLogout = () => {
    signOut(auth);
  };

  const handleNavigate = (view: View) => {
    console.log('Navigating to:', view); // Debug log
    window.scrollTo(0, 0);
    setCurrentView(view);
  };

  const handleLoginRequest = () => {
    if(!firebaseUser) setShowLoginModal(true);
  }

  const allFavoriteIds = useMemo(() => {
    return Array.from(new Set(userLists.flatMap(list => list.gemIds)));
  }, [userLists]);

  const handleSaveRequest = (gemId: string) => {
    setGemToSaveId(gemId);
    setIsSaveModalOpen(true);
  };

  // Aggiorna le liste dell'utente con la nuova struttura
  const updateUserLists = async (updatedLists: ListWithItems[]) => {
      if (!firebaseUser) return;
      setUserLists(updatedLists);
      // Non serve più salvare nel documento utente, le liste sono in collezioni separate
  };

  const handleSaveToList = async (listId: string) => {
    if (!gemToSaveId || !firebaseUser) return;

    try {
      await firestoreService.addGemToUserList(firebaseUser.uid, listId, gemToSaveId);

      // Aggiorna lo stato locale
      const updatedLists = userLists.map(list => {
        if (list.id === listId && !list.gemIds.includes(gemToSaveId)) {
          return {
            ...list,
            gemIds: [...list.gemIds, gemToSaveId],
            itemCount: list.itemCount + 1,
            updatedAt: new Date()
          };
        }
        return list;
      });
      setUserLists(updatedLists);
    } catch (error) {
      console.error('Error saving to list:', error);
      alert('Errore nel salvare nella lista');
    }
  };

  const handleCreateListAndSave = async (listName: string) => {
      if (!gemToSaveId || !firebaseUser) return;

      try {
        const newListId = await firestoreService.createNewList(firebaseUser.uid, listName);
        await firestoreService.addGemToUserList(firebaseUser.uid, newListId, gemToSaveId);

        // Aggiungi la nuova lista allo stato locale
        const newList: ListWithItems = {
          id: newListId,
          name: listName,
          isPublic: false,
          createdBy: firebaseUser.uid,
          createdAt: new Date(),
          updatedAt: new Date(),
          gemIds: [gemToSaveId],
          itemCount: 1,
          userRole: 'owner'
        };
        setUserLists([...userLists, newList]);
      } catch (error) {
        console.error('Error creating list and saving:', error);
        alert('Errore nella creazione della lista');
      }
  };

  // Crea una nuova lista tramite il servizio e ricarica le liste
  const handleCreateNewList = async (listName: string) => {
    if (!firebaseUser) return;

    try {
      await firestoreService.createNewList(firebaseUser.uid, listName);

      // Ricarica le liste dopo la creazione
      const updatedLists = await firestoreService.fetchUserListsNew(firebaseUser.uid);
      setUserLists(updatedLists);
    } catch (error) {
      console.error('Error creating new list:', error);
      throw error; // Rilancia l'errore per gestirlo nel componente
    }
  };

  const handleToggleFavorite = async (gemId: string) => {
    if (!firebaseUser) return;

    const favoritesList = userLists.find(list => list.name === 'Preferiti' || list.id === 'default');
    if (!favoritesList) return;

    const isFav = favoritesList.gemIds.includes(gemId);

    try {
      if (isFav) {
        await firestoreService.removeGemFromUserList(firebaseUser.uid, favoritesList.id, gemId);
      } else {
        await firestoreService.addGemToUserList(firebaseUser.uid, favoritesList.id, gemId);
      }

      // Aggiorna lo stato locale
      const updatedLists = userLists.map(list => {
        if (list.id === favoritesList.id) {
          return {
            ...list,
            gemIds: isFav
              ? list.gemIds.filter(id => id !== gemId)
              : [...list.gemIds, gemId],
            itemCount: isFav ? list.itemCount - 1 : list.itemCount + 1,
            updatedAt: new Date()
          };
        }
        return list;
      });
      setUserLists(updatedLists);
    } catch (error) {
      console.error('Error toggling favorite:', error);
      alert('Errore nel modificare i preferiti');
    }
  };

  const handleAddUserQuestion = async (gemId: string, question: string) => {
      await firestoreService.addUserQuestion(gemId, question);
  };

  const handleSelectGem = (gemId: string) => {
      setSelectedGemId(gemId);
      handleNavigate('detail');
  };

  const handleBackToFeed = () => {
      setSelectedGemId(null);
      handleNavigate('feed');
  };

  const handleSelectTag = (tag: string) => {
      setFilter({ type: 'tag', value: tag });
      handleBackToFeed();
  };

  const handleUpdateUser = async (updatedUser: User) => {
      if (!firebaseUser) return;
      setUser(updatedUser);
      await firestoreService.updateUserProfile(firebaseUser.uid, {
          firstName: updatedUser.firstName,
          lastName: updatedUser.lastName,
      });
      alert("Profilo aggiornato!");
  }

  // Funzioni per il sistema di feedback
  const getCurrentSection = (): string => {
    switch (currentView) {
      case 'feed':
        return 'Feed principale';
      case 'detail':
        return `Dettaglio gem: ${selectedGem?.title || 'Sconosciuto'}`;
      case 'saved':
        return 'Liste salvate';
      case 'profile':
        return 'Profilo utente';
      case 'dashboard':
        return 'Dashboard amministratore';
      case 'topics':
        return 'Gestione argomenti';
      default:
        return 'Sezione sconosciuta';
    }
  };

  const handleFeedbackSubmit = async (message: string) => {
    if (!firebaseUser || !user) return;

    try {
      const userName = `${user.firstName} ${user.lastName}`;
      await feedbackService.createFeedback(
        firebaseUser.uid,
        user.email,
        userName,
        getCurrentSection(),
        message
      );
      // Mostra animazione di successo sul pulsante invece dell'alert
      if (feedbackButtonRef.current?.showSuccess) {
        feedbackButtonRef.current.showSuccess();
      }
    } catch (error) {
      console.error('Errore nell\'invio del feedback:', error);
      throw error; // Permette alla modale di gestire l'errore
    }
  };

  // Verifica se l'utente può vedere il pulsante feedback
  const canShowFeedbackButton = useMemo(() => {
    return user && (user.role === UserRole.ADMIN || user.role === UserRole.BETATESTER);
  }, [user]);

  const filteredGems = gems.filter(gem => {
      if (!gem.tags) return false; // Safety check
      switch (filter.type) {
        case 'all':
            return true;
        case 'favorites':
            return firebaseUser ? allFavoriteIds.includes(gem.id) : false;
        case 'topic':
            return gem.topic === filter.value;
        case 'channel':
            const channel = channels.find(c => c.id === filter.value);
            if (channel) {
                return gem.tags.some(tag => channel.filterTags.includes(tag.toLowerCase()));
            }
            return false;
        case 'tag':
            return gem.tags.map(t => t.toLowerCase()).includes(filter.value.toLowerCase());
        default:
            return true;
      }
  });

  const selectedGem = gems.find(gem => gem.id === selectedGemId);

  const renderFeed = () => (
    <>
        <Header
            isLoggedIn={!!firebaseUser}
            user={user}
            onLogin={() => setShowLoginModal(true)}
            onLogout={handleLogout}
            selectedFilter={filter}
            onSelectFilter={setFilter}
            onNavigate={handleNavigate}
            channels={channels}
        />
        <main className="max-w-xl mx-auto py-8 px-4 sm:px-6 lg:px-8">
        {isLoading && gems.length === 0 ? (
          <div className="flex flex-col items-center justify-center text-center text-slate-500 dark:text-slate-400 pt-20">
            <SparklesIcon className="w-16 h-16 animate-pulse text-indigo-400"/>
            <p className="mt-4 text-lg font-semibold">Stiamo preparando Curiow per te...</p>
            <p className="mt-1 text-sm">Un momento, stiamo cercando spunti interessanti.</p>
          </div>
        ) : (
          <div className="space-y-8">
            {filteredGems.length > 0 ? (
                filteredGems.map(gem => (
                    <GemCard
                        key={gem.id}
                        gem={gem}
                        isLoggedIn={!!firebaseUser}
                        isFavorite={allFavoriteIds.includes(gem.id)}
                        onSaveRequest={handleSaveRequest}
                        onSelect={handleSelectGem}
                        onLoginRequest={handleLoginRequest}
                    />
                ))
            ) : (
                <div className="text-center pt-20 text-slate-500 dark:text-slate-400">
                    <h3 className="text-xl font-semibold">Nessuna gemma trovata</h3>
                    <p className="mt-2">Prova a selezionare un'altra categoria o filtro.</p>
                </div>
            )}
          </div>
        )}
      </main>
    </>
  );

  const renderContent = () => {
    switch (currentView) {
        case 'detail':
            return selectedGem ? (
                <GemDetailView
                    gem={selectedGem}
                    isFavorite={allFavoriteIds.includes(selectedGem.id)}
                    onBack={handleBackToFeed}
                    onSaveRequest={handleSaveRequest}
                    onAddUserQuestion={handleAddUserQuestion}
                    onTagSelect={handleSelectTag}
                />
            ) : renderFeed(); // Fallback to feed if no gem selected
        case 'saved':
            return firebaseUser ? <SavedView 
                        allGems={gems} 
                        allFavoriteIds={allFavoriteIds}
                        savedLists={userLists}
                        onUpdateLists={updateUserLists}
                        onSelectGem={handleSelectGem}
                        onToggleFavorite={handleToggleFavorite}
                        onLoginRequest={handleLoginRequest}
                        onBack={() => handleNavigate('feed')}
                        onCreateList={handleCreateNewList}
                    /> : renderFeed();
        case 'profile':
            return firebaseUser && user ? <ProfileView user={user} onUpdateUser={handleUpdateUser} onBack={() => handleNavigate('feed')} onNavigate={handleNavigate} /> : renderFeed();
        case 'dashboard':
            return firebaseUser && user ? (
                <AdminDashboard
                    currentUser={{ ...user, id: firebaseUser.uid }}
                    onClose={() => handleNavigate('feed')}
                />
            ) : renderFeed();
        case 'topics':
            return firebaseUser && user ? (
                <TopicManagement
                    currentUser={{ ...user, uid: firebaseUser.uid }}
                    onBack={() => handleNavigate('feed')}
                />
            ) : renderFeed();
        case 'feed':
        default:
            return renderFeed();
    }
  }

  return (
    <div className="min-h-screen bg-slate-50 dark:bg-slate-900">
        {renderContent()}

        {/* Pulsante feedback flottante per admin e betatester */}
        {canShowFeedbackButton && (
          <FeedbackButton
            ref={feedbackButtonRef}
            onClick={() => setShowFeedbackModal(true)}
          />
        )}

        {showLoginModal && <LoginModal
            onLoginAttempt={handleLoginAttempt}
            onSignUpAttempt={handleSignUpAttempt}
            onGoogleAuth={handleGoogleAuth}
            onCancel={() => setShowLoginModal(false)}
        />}

        {showFeedbackModal && (
          <FeedbackModal
            section={getCurrentSection()}
            onSubmit={handleFeedbackSubmit}
            onCancel={() => setShowFeedbackModal(false)}
            onSuccess={() => {
              if (feedbackButtonRef.current?.showSuccess) {
                feedbackButtonRef.current.showSuccess();
              }
            }}
          />
        )}

        {isSaveModalOpen && gemToSaveId && (
            <SaveToListModal
                isOpen={isSaveModalOpen}
                onClose={() => setIsSaveModalOpen(false)}
                lists={userLists.filter(l => l.id !== 'default')}
                gemId={gemToSaveId}
                onSaveToList={handleSaveToList}
                onCreateAndSave={handleCreateListAndSave}
                onToggleDefaultFavorite={handleToggleFavorite}
                isSavedToDefault={userLists.find(l=>l.id==='default')?.gemIds.includes(gemToSaveId) ?? false}
            />
        )}
    </div>
  );
};

export default App;

import React, { useState, useEffect, useMemo } from 'react';
import { Gem, User, SavedList, Channel, Filter, Topic, ListWithItems, UserRole } from './types';
import { auth, googleProvider } from './services/firebase';
import { onAuthStateChanged, User as FirebaseUser, createUserWithEmailAndPassword, signInWithEmailAndPassword, signOut, signInWithPopup } from 'firebase/auth';
import * as firestoreService from './services/firestoreService';
import * as feedbackService from './services/feedbackService';
import Header from './components/Header';
import GemCard from './components/GemCard';
import GemDetailView from './components/GemDetailView';
import LoginModal from './components/LoginModal';
import SavedView from './components/SavedView';
import ProfileView from './components/ProfileView';
import AdminDashboard from './components/admin/AdminDashboard';
import TopicManagement from './components/admin/TopicManagement';
import FeedbackButton from './components/FeedbackButton';
import FeedbackModal from './components/FeedbackModal';
import { SparklesIcon } from './components/icons';
import SaveToListModal from './components/SaveToListModal';
// Import admin utils in development
import './utils/adminUtils';

type View = 'feed' | 'detail' | 'saved' | 'profile' | 'dashboard' | 'topics';

const App: React.FC = () => {
  const [gems, setGems] = useState<Gem[]>([]);
  const [channels, setChannels] = useState<Channel[]>([]);
  const [isLoading, setIsLoading] = useState(true);

  const [firebaseUser, setFirebaseUser] = useState<FirebaseUser | null>(null);
  const [user, setUser] = useState<User | null>(null);
  const [userLists, setUserLists] = useState<ListWithItems[]>([]);
  const [isMigrated, setIsMigrated] = useState(false);

  const [filter, setFilter] = useState<Filter>({ type: 'all' });
  const [showLoginModal, setShowLoginModal] = useState(false);
  const [currentView, setCurrentView] = useState<View>('feed');
  const [selectedGemId, setSelectedGemId] = useState<string | null>(null);

  const [isSaveModalOpen, setIsSaveModalOpen] = useState(false);
  const [gemToSaveId, setGemToSaveId] = useState<string | null>(null);

  // Stati per il sistema di feedback
  const [showFeedbackModal, setShowFeedbackModal] = useState(false);
  const feedbackButtonRef = React.useRef<any>(null);

  // Fetch initial static-like data
  useEffect(() => {
    const fetchInitialData = async () => {
        setIsLoading(true);
        const [fetchedGems, fetchedChannels] = await Promise.all([
            firestoreService.fetchGems(),
            firestoreService.fetchChannels(),
        ]);
        setGems(fetchedGems);
        setChannels(fetchedChannels);
        setIsLoading(false);
    };
    fetchInitialData();
  }, []);

  // Auth state listener
  useEffect(() => {
      const unsubscribe = onAuthStateChanged(auth, async (currentUser) => {
          console.log('Auth state changed - currentUser:', currentUser?.uid);
          setFirebaseUser(currentUser);
          if (currentUser) {
              let userProfile = await firestoreService.fetchUserProfile(currentUser.uid);
              console.log('Fetched user profile:', userProfile);

              // Se il profilo non esiste, creane uno nuovo. Utile per utenti già esistenti in Auth ma non in Firestore.
              if (!userProfile) {
                  console.log(`Creating new profile for user ${currentUser.uid}`);
                  const email = currentUser.email || 'no-email@example.com';
                  const [firstName, lastName] = email.split('@')[0].split('.') || [email, ''];
                  await firestoreService.createUserProfile(currentUser.uid, email, firstName, lastName || '');
                  userProfile = await firestoreService.fetchUserProfile(currentUser.uid);
                  console.log('Created new user profile:', userProfile);
              }

              // Migrazione automatica alle nuove liste
              console.log('Attempting migration to new list structure...');
              const migrationSuccess = await firestoreService.migrateUserToNewListStructure(currentUser.uid);
              setIsMigrated(migrationSuccess);

              // Carica le liste con la nuova struttura
              const newUserLists = await firestoreService.fetchUserListsNew(currentUser.uid);
              setUser(userProfile);
              setUserLists(newUserLists);
              setShowLoginModal(false);

              console.log('Migration completed:', migrationSuccess);
              console.log('Loaded user lists:', newUserLists);
          } else {
              // User is signed out
              setUser(null);
              setUserLists([]);
              setIsMigrated(false);
              setFilter({ type: 'all' });
              setCurrentView('feed');
          }
      });
      return () => unsubscribe();
  }, []);

  // Listener for user questions on selected gem
  useEffect(() => {
    if (!selectedGemId) return;

    const unsubscribe = firestoreService.listenToUserQuestions(selectedGemId, (questions) => {
        setGems(prevGems => prevGems.map(gem => 
            gem.id === selectedGemId ? { ...gem, userQuestions: questions } : gem
        ));
    });

    return () => unsubscribe();
  }, [selectedGemId]);

  const handleSignUpAttempt = async (email: string, pass: string, firstName: string, lastName: string) => {
      const userCredential = await createUserWithEmailAndPassword(auth, email, pass);
      await firestoreService.createUserProfile(userCredential.user.uid, email, firstName, lastName);
  };

  const handleLoginAttempt = async (email: string, pass: string) => {
      await signInWithEmailAndPassword(auth, email, pass);
  };

  const handleGoogleAuth = async () => {
    try {
      const result = await signInWithPopup(auth, googleProvider);
      const user = result.user;

      // Controlla se il profilo utente esiste già
      let userProfile = await firestoreService.fetchUserProfile(user.uid);

      if (!userProfile) {
        // Se è la prima volta che l'utente accede con Google, crea il profilo
        const email = user.email || 'no-email@example.com';
        const firstName = user.displayName?.split(' ')[0] || 'Nome';
        const lastName = user.displayName?.split(' ').slice(1).join(' ') || 'Cognome';

        await firestoreService.createUserProfile(user.uid, email, firstName, lastName);
      }

      // Il resto della logica di login è gestita automaticamente dall'listener onAuthStateChanged
    } catch (error: any) {
      console.error('Errore nell\'autenticazione Google:', error);
      throw new Error('Errore nell\'autenticazione con Google: ' + error.message);
    }
  };

  const handleLogout = () => {
    signOut(auth);
  };

  const handleNavigate = (view: View) => {
    console.log('Navigating to:', view); // Debug log
    window.scrollTo(0, 0);
    setCurrentView(view);
  };

  const handleLoginRequest = () => {
    if(!firebaseUser) setShowLoginModal(true);
  }

  const allFavoriteIds = useMemo(() => {
    return Array.from(new Set(userLists.flatMap(list => list.gemIds)));
  }, [userLists]);

  const handleSaveRequest = (gemId: string) => {
    setGemToSaveId(gemId);
    setIsSaveModalOpen(true);
  };

  // Aggiorna le liste dell'utente con la nuova struttura
  const updateUserLists = async (updatedLists: ListWithItems[]) => {
      if (!firebaseUser) return;
      setUserLists(updatedLists);
      // Non serve più salvare nel documento utente, le liste sono in collezioni separate
  };

  const handleSaveToList = async (listId: string) => {
    if (!gemToSaveId || !firebaseUser) return;

    try {
      await firestoreService.addGemToUserList(firebaseUser.uid, listId, gemToSaveId);

      // Aggiorna lo stato locale
      const updatedLists = userLists.map(list => {
        if (list.id === listId && !list.gemIds.includes(gemToSaveId)) {
          return {
            ...list,
            gemIds: [...list.gemIds, gemToSaveId],
            itemCount: list.itemCount + 1,
            updatedAt: new Date()
          };
        }
        return list;
      });
      setUserLists(updatedLists);
    } catch (error) {
      console.error('Error saving to list:', error);
      alert('Errore nel salvare nella lista');
    }
  };

  const handleCreateListAndSave = async (listName: string) => {
      if (!gemToSaveId || !firebaseUser) return;

      try {
        const newListId = await firestoreService.createNewList(firebaseUser.uid, listName);
        await firestoreService.addGemToUserList(firebaseUser.uid, newListId, gemToSaveId);

        // Aggiungi la nuova lista allo stato locale
        const newList: ListWithItems = {
          id: newListId,
          name: listName,
          isPublic: false,
          createdBy: firebaseUser.uid,
          createdAt: new Date(),
          updatedAt: new Date(),
          gemIds: [gemToSaveId],
          itemCount: 1,
          userRole: 'owner'
        };
        setUserLists([...userLists, newList]);
      } catch (error) {
        console.error('Error creating list and saving:', error);
        alert('Errore nella creazione della lista');
      }
  };

  // Crea una nuova lista tramite il servizio e ricarica le liste
  const handleCreateNewList = async (listName: string) => {
    if (!firebaseUser) return;

    try {
      await firestoreService.createNewList(firebaseUser.uid, listName);

      // Ricarica le liste dopo la creazione
      const updatedLists = await firestoreService.fetchUserListsNew(firebaseUser.uid);
      setUserLists(updatedLists);
    } catch (error) {
      console.error('Error creating new list:', error);
      throw error; // Rilancia l'errore per gestirlo nel componente
    }
  };

  const handleToggleFavorite = async (gemId: string) => {
    if (!firebaseUser) return;

    const favoritesList = userLists.find(list => list.name === 'Preferiti' || list.id === 'default');
    if (!favoritesList) return;

    const isFav = favoritesList.gemIds.includes(gemId);

    try {
      if (isFav) {
        await firestoreService.removeGemFromUserList(firebaseUser.uid, favoritesList.id, gemId);
      } else {
        await firestoreService.addGemToUserList(firebaseUser.uid, favoritesList.id, gemId);
      }

      // Aggiorna lo stato locale
      const updatedLists = userLists.map(list => {
        if (list.id === favoritesList.id) {
          return {
            ...list,
            gemIds: isFav
              ? list.gemIds.filter(id => id !== gemId)
              : [...list.gemIds, gemId],
            itemCount: isFav ? list.itemCount - 1 : list.itemCount + 1,
            updatedAt: new Date()
          };
        }
        return list;
      });
      setUserLists(updatedLists);
    } catch (error) {
      console.error('Error toggling favorite:', error);
      alert('Errore nel modificare i preferiti');
    }
  };

  const handleAddUserQuestion = async (gemId: string, question: string) => {
      await firestoreService.addUserQuestion(gemId, question);
  };

  const handleSelectGem = (gemId: string) => {
      setSelectedGemId(gemId);
      handleNavigate('detail');
  };

  const handleBackToFeed = () => {
      setSelectedGemId(null);
      handleNavigate('feed');
  };

  const handleSelectTag = (tag: string) => {
      setFilter({ type: 'tag', value: tag });
      handleBackToFeed();
  };

  const handleUpdateUser = async (updatedUser: User) => {
      if (!firebaseUser) return;
      setUser(updatedUser);
      await firestoreService.updateUserProfile(firebaseUser.uid, {
          firstName: updatedUser.firstName,
          lastName: updatedUser.lastName,
      });
      alert("Profilo aggiornato!");
  }

  // Funzioni per il sistema di feedback
  const getCurrentSection = (): string => {
    switch (currentView) {
      case 'feed':
        return 'Feed principale';
      case 'detail':
        return `Dettaglio gem: ${selectedGem?.title || 'Sconosciuto'}`;
      case 'saved':
        return 'Liste salvate';
      case 'profile':
        return 'Profilo utente';
      case 'dashboard':
        return 'Dashboard amministratore';
      case 'topics':
        return 'Gestione argomenti';
      default:
        return 'Sezione sconosciuta';
    }
  };

  const handleFeedbackSubmit = async (section: string, message: string) => {
    if (!firebaseUser || !user) return;

    try {
      const userName = `${user.firstName} ${user.lastName}`;
      await feedbackService.createFeedback(
        firebaseUser.uid,
        user.email,
        userName,
        section,
        message
      );
      // Mostra animazione di successo sul pulsante invece dell'alert
      if (feedbackButtonRef.current?.showSuccess) {
        feedbackButtonRef.current.showSuccess();
      }
    } catch (error) {
      console.error('Errore nell\'invio del feedback:', error);
      throw error; // Permette alla modale di gestire l'errore
    }
  };

  // Verifica se l'utente può vedere il pulsante feedback
  const canShowFeedbackButton = useMemo(() => {
    return user && (user.role === UserRole.ADMIN || user.role === UserRole.BETATESTER);
  }, [user]);

  const filteredGems = gems.filter(gem => {
      if (!gem.tags) return false; // Safety check
      switch (filter.type) {
        case 'all':
            return true;
        case 'favorites':
            return firebaseUser ? allFavoriteIds.includes(gem.id) : false;
        case 'topic':
            return gem.topic === filter.value;
        case 'channel':
            const channel = channels.find(c => c.id === filter.value);
            if (channel) {
                return gem.tags.some(tag => channel.filterTags.includes(tag.toLowerCase()));
            }
            return false;
        case 'tag':
            return gem.tags.map(t => t.toLowerCase()).includes(filter.value.toLowerCase());
        default:
            return true;
      }
  });

  const selectedGem = gems.find(gem => gem.id === selectedGemId);

  const renderFeed = () => (
    <>
        <Header
            isLoggedIn={!!firebaseUser}
            user={user}
            onLogin={() => setShowLoginModal(true)}
            onLogout={handleLogout}
            selectedFilter={filter}
            onSelectFilter={setFilter}
            onNavigate={handleNavigate}
            channels={channels}
        />
        <main className="max-w-xl mx-auto py-8 px-4 sm:px-6 lg:px-8">
        {isLoading && gems.length === 0 ? (
          <div className="flex flex-col items-center justify-center text-center text-slate-500 dark:text-slate-400 pt-20">
            <SparklesIcon className="w-16 h-16 animate-pulse text-indigo-400"/>
            <p className="mt-4 text-lg font-semibold">Stiamo preparando Curiow per te...</p>
            <p className="mt-1 text-sm">Un momento, stiamo cercando spunti interessanti.</p>
          </div>
        ) : (
          <div className="space-y-8">
            {filteredGems.length > 0 ? (
                filteredGems.map(gem => (
                    <GemCard
                        key={gem.id}
                        gem={gem}
                        isLoggedIn={!!firebaseUser}
                        isFavorite={allFavoriteIds.includes(gem.id)}
                        onSaveRequest={handleSaveRequest}
                        onSelect={handleSelectGem}
                        onLoginRequest={handleLoginRequest}
                    />
                ))
            ) : (
                <div className="text-center pt-20 text-slate-500 dark:text-slate-400">
                    <h3 className="text-xl font-semibold">Nessuna gemma trovata</h3>
                    <p className="mt-2">Prova a selezionare un'altra categoria o filtro.</p>
                </div>
            )}
          </div>
        )}
      </main>
    </>
  );

  const renderContent = () => {
    switch (currentView) {
        case 'detail':
            return selectedGem ? (
                <GemDetailView
                    gem={selectedGem}
                    isFavorite={allFavoriteIds.includes(selectedGem.id)}
                    onBack={handleBackToFeed}
                    onSaveRequest={handleSaveRequest}
                    onAddUserQuestion={handleAddUserQuestion}
                    onTagSelect={handleSelectTag}
                />
            ) : renderFeed(); // Fallback to feed if no gem selected
        case 'saved':
            return firebaseUser ? <SavedView 
                        allGems={gems} 
                        allFavoriteIds={allFavoriteIds}
                        savedLists={userLists}
                        onUpdateLists={updateUserLists}
                        onSelectGem={handleSelectGem}
                        onToggleFavorite={handleToggleFavorite}
                        onLoginRequest={handleLoginRequest}
                        onBack={() => handleNavigate('feed')}
                        onCreateList={handleCreateNewList}
                    /> : renderFeed();
        case 'profile':
            return firebaseUser && user ? <ProfileView user={user} onUpdateUser={handleUpdateUser} onBack={() => handleNavigate('feed')} onNavigate={handleNavigate} /> : renderFeed();
        case 'dashboard':
            return firebaseUser && user ? (
                <AdminDashboard
                    currentUser={{ ...user, id: firebaseUser.uid }}
                    onClose={() => handleNavigate('feed')}
                />
            ) : renderFeed();
        case 'topics':
            return firebaseUser && user ? (
                <TopicManagement
                    currentUser={{ ...user, uid: firebaseUser.uid }}
                    onBack={() => handleNavigate('feed')}
                />
            ) : renderFeed();
        case 'feed':
        default:
            return renderFeed();
    }
  }

  return (
    <div className="min-h-screen bg-slate-50 dark:bg-slate-900">
        {renderContent()}

        {/* Pulsante feedback flottante per admin e betatester */}
        {canShowFeedbackButton && (
          <FeedbackButton
            ref={feedbackButtonRef}
            onClick={() => setShowFeedbackModal(true)}
          />
        )}

        {showLoginModal && <LoginModal
            onLoginAttempt={handleLoginAttempt}
            onSignUpAttempt={handleSignUpAttempt}
            onGoogleAuth={handleGoogleAuth}
            onCancel={() => setShowLoginModal(false)}
        />}

        {showFeedbackModal && (
          <FeedbackModal
            section={getCurrentSection()}
            onSubmit={handleFeedbackSubmit}
            onCancel={() => setShowFeedbackModal(false)}
            onSuccess={() => {
              if (feedbackButtonRef.current?.showSuccess) {
                feedbackButtonRef.current.showSuccess();
              }
            }}
          />
        )}

        {isSaveModalOpen && gemToSaveId && (
            <SaveToListModal
                isOpen={isSaveModalOpen}
                onClose={() => setIsSaveModalOpen(false)}
                lists={userLists.filter(l => l.id !== 'default')}
                gemId={gemToSaveId}
                onSaveToList={handleSaveToList}
                onCreateAndSave={handleCreateListAndSave}
                onToggleDefaultFavorite={handleToggleFavorite}
                isSavedToDefault={userLists.find(l=>l.id==='default')?.gemIds.includes(gemToSaveId) ?? false}
            />
        )}
    </div>
  );
};

export default App;

import React from 'react';

interface FeedbackButtonProps {
  onClick: () => void;
}

const FeedbackButton: React.FC<FeedbackButtonProps> = ({ onClick }) => {
  return (
    <button
      onClick={onClick}
      className="fixed right-6 top-1/2 transform -translate-y-1/2 z-40
                 bg-blue-600 hover:bg-blue-700 text-white
                 rounded-full p-4 shadow-lg transition-all duration-300
                 hover:scale-110 focus:outline-none focus:ring-4 focus:ring-blue-300"
      title="Dai feedback"
    >
      <svg
        className="w-6 h-6"
        fill="none"
        stroke="currentColor"
        viewBox="0 0 24 24"
      >
        <path
          strokeLinecap="round"
          strokeLinejoin="round"
          strokeWidth={2}
          d="M8 12h.01M12 12h.01M16 12h.01M21 12c0 4.418-4.03 8-9 8a9.863 9.863 0 01-4.255-.949L3 20l1.395-3.72C3.512 15.042 3 13.574 3 12c0-4.418 4.03-8 9-8s9 3.582 9 8z"
        />
      </svg>
    </button>
  );
};

export default FeedbackButton;

import React, { useState, forwardRef, useImperativeHandle } from 'react';

interface FeedbackButtonProps {
  onClick: () => void;
}

export interface FeedbackButtonRef {
  showSuccess: () => void;
}

const FeedbackButton = forwardRef<FeedbackButtonRef, FeedbackButtonProps>(({ onClick }, ref) => {
  const [showSuccess, setShowSuccess] = useState(false);

  const handleClick = () => {
    onClick();
  };

  const showSuccessAnimation = () => {
    setShowSuccess(true);
    setTimeout(() => {
      setShowSuccess(false);
    }, 2000);
  };

  useImperativeHandle(ref, () => ({
    showSuccess: showSuccessAnimation
  }));

  return (
    <button
      onClick={handleClick}
      className={`fixed right-6 top-1/2 transform -translate-y-1/2 z-40
                 ${showSuccess ? 'bg-green-600' : 'bg-blue-600 hover:bg-blue-700'} text-white
                 rounded-full p-4 shadow-lg transition-all duration-300
                 hover:scale-110 focus:outline-none focus:ring-4 
                 ${showSuccess ? 'focus:ring-green-300' : 'focus:ring-blue-300'}`}
      title="Dai feedback"
    >
      {showSuccess ? (
        <svg
          className="w-6 h-6"
          fill="none"
          stroke="currentColor"
          viewBox="0 0 24 24"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={3}
            d="M5 13l4 4L19 7"
          />
        </svg>
      ) : (
        <svg
          className="w-6 h-6"
          fill="none"
          stroke="currentColor"
          viewBox="0 0 24 24"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M8 12h.01M12 12h.01M16 12h.01M21 12c0 4.418-4.03 8-9 8a9.863 9.863 0 01-4.255-.949L3 20l1.395-3.72C3.512 15.042 3 13.574 3 12c0-4.418 4.03-8 9-8s9 3.582 9 8z"
          />
        </svg>
      )}
    </button>
  );
});

FeedbackButton.displayName = 'FeedbackButton';

export default FeedbackButton;

import React, { useState } from 'react';

interface FeedbackModalProps {
  section: string;
  onSubmit: (message: string) => Promise<void>;
  onCancel: () => void;
  onSuccess?: () => void;
}

const FeedbackModal: React.FC<FeedbackModalProps> = ({ section, onSubmit, onCancel, onSuccess }) => {
  const [message, setMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!message.trim()) {
      setError('Inserisci un messaggio di feedback');
      return;
    }

    setIsLoading(true);
    setError('');

    try {
      await onSubmit(message.trim());
      onSuccess?.(); // Chiama il callback onSuccess se fornito
      onCancel(); // Chiudi la modale dopo il successo
    } catch (err) {
      setError('Errore nell\'invio del feedback. Riprova.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-xl max-w-md w-full max-h-[90vh] overflow-y-auto">
        <div className="p-6">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-xl font-bold text-gray-900">Feedback Betatester</h2>
            <button
              onClick={onCancel}
              className="text-gray-400 hover:text-gray-600 transition-colors"
              disabled={isLoading}
            >
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Sezione
              </label>
              <input
                type="text"
                value={section}
                disabled
                className="w-full px-3 py-2 border border-gray-300 rounded-lg bg-gray-50 text-gray-600"
              />
            </div>

            <div>
              <label htmlFor="feedback-message" className="block text-sm font-medium text-gray-700 mb-2">
                Messaggio di feedback *
              </label>
              <textarea
                id="feedback-message"
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                placeholder="Descrivi il tuo feedback, suggerimenti o problemi riscontrati..."
                rows={6}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none"
                disabled={isLoading}
                maxLength={2000}
              />
              <div className="text-xs text-gray-500 mt-1">
                {message.length}/2000 caratteri
              </div>
            </div>

            {error && (
              <div className="text-red-600 text-sm bg-red-50 p-3 rounded-lg">
                {error}
              </div>
            )}

            <div className="flex space-x-3 pt-4">
              <button
                type="button"
                onClick={onCancel}
                className="flex-1 px-4 py-2 text-gray-700 bg-gray-100 rounded-lg hover:bg-gray-200 transition-colors"
                disabled={isLoading}
              >
                Annulla
              </button>
              <button
                type="submit"
                disabled={isLoading || !message.trim()}
                className="flex-1 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
              >
                {isLoading ? 'Invio...' : 'Invia Feedback'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default FeedbackModal;

import React, { useState } from 'react';

interface FeedbackModalProps {
  section: string;
  onSubmit: (section: string, message: string) => Promise<void>;
  onCancel: () => void;
  onSuccess?: () => void;
}

const FeedbackModal: React.FC<FeedbackModalProps> = ({ section, onSubmit, onCancel, onSuccess }) => {
  const [currentSection, setCurrentSection] = useState(section);
  const [message, setMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!currentSection.trim()) {
      setError('Inserisci una sezione');
      return;
    }

    if (!message.trim()) {
      setError('Inserisci un messaggio di feedback');
      return;
    }

    setIsLoading(true);
    setError('');

    try {
      await onSubmit(currentSection.trim(), message.trim());
      onSuccess?.(); // Chiama il callback onSuccess se fornito
      onCancel(); // Chiudi la modale dopo il successo
    } catch (err) {
      setError('Errore nell\'invio del feedback. Riprova.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-xl max-w-md w-full max-h-[90vh] overflow-y-auto">
        <div className="p-6">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-xl font-bold text-gray-900">Feedback Betatester</h2>
            <button
              onClick={onCancel}
              className="text-gray-400 hover:text-gray-600 transition-colors"
              disabled={isLoading}
            >
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label htmlFor="feedback-section" className="block text-sm font-medium text-gray-700 mb-2">
                Sezione *
              </label>
              <input
                id="feedback-section"
                type="text"
                value={currentSection}
                onChange={(e) => setCurrentSection(e.target.value)}
                placeholder="Es. Feed principale, Dettaglio gem, ecc."
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                disabled={isLoading}
                maxLength={100}
              />
            </div>

            <div>
              <label htmlFor="feedback-message" className="block text-sm font-medium text-gray-700 mb-2">
                Messaggio di feedback *
              </label>
              <textarea
                id="feedback-message"
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                placeholder="Descrivi il tuo feedback, suggerimenti o problemi riscontrati..."
                rows={6}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none"
                disabled={isLoading}
                maxLength={2000}
              />
              <div className="text-xs text-gray-500 mt-1">
                {message.length}/2000 caratteri
              </div>
            </div>

            {error && (
              <div className="text-red-600 text-sm bg-red-50 p-3 rounded-lg">
                {error}
              </div>
            )}

            <div className="flex space-x-3 pt-4">
              <button
                type="button"
                onClick={onCancel}
                className="flex-1 px-4 py-2 text-gray-700 bg-gray-100 rounded-lg hover:bg-gray-200 transition-colors"
                disabled={isLoading}
              >
                Annulla
              </button>
              <button
                type="submit"
                disabled={isLoading || !message.trim()}
                className="flex-1 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
              >
                {isLoading ? 'Invio...' : 'Invia Feedback'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default FeedbackModal;

import React, { useState, useRef, useEffect } from 'react';
import { TOPICS } from '../constants';
import { Topic, Channel, Filter, User } from '../types';
import { SparklesIcon, UserCircleIcon, BookmarkSquareIcon, Cog6ToothIcon, TagIcon, ChevronDownIcon } from './icons';
import { useUserPermissions } from '../services/roleService';

interface HeaderProps {
  isLoggedIn: boolean;
  user?: User | null;
  onLogin: () => void;
  onLogout: () => void;
  selectedFilter: Filter;
  onSelectFilter: (filter: Filter) => void;
  onNavigate: (view: 'feed' | 'saved' | 'profile' | 'dashboard' | 'topics') => void;
  channels: Channel[];
}

const Header: React.FC<HeaderProps> = ({ isLoggedIn, user, onLogin, onLogout, selectedFilter, onSelectFilter, onNavigate, channels }) => {
  const [showProfileMenu, setShowProfileMenu] = useState(false);
  const menuRef = useRef<HTMLDivElement>(null);

  const permissions = useUserPermissions(user);


  const getButtonClass = (filter: Filter) => {
    const baseClass = 'px-3 py-1.5 text-sm font-semibold rounded-full transition-colors duration-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-slate-50 dark:focus:ring-offset-slate-900 focus:ring-indigo-500 whitespace-nowrap';

    if (selectedFilter.type === filter.type && ('value' in selectedFilter && 'value' in filter ? selectedFilter.value === filter.value : true)) {
      return `${baseClass} bg-indigo-600 text-white shadow-md`;
    }
    return `${baseClass} bg-white dark:bg-slate-800 text-slate-700 dark:text-slate-300 hover:bg-slate-100 dark:hover:bg-slate-700`;
  };

  return (
    <header className="sticky top-0 z-30 bg-slate-50/80 dark:bg-slate-900/80 backdrop-blur-lg border-b border-slate-200 dark:border-slate-700/50">
      <div className="max-w-5xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          <button onClick={() => { onSelectFilter({ type: 'all' }); onNavigate('feed'); }} className="flex items-center space-x-2">
            <SparklesIcon className="w-7 h-7 text-indigo-500" />
            <h1 className="text-xl font-bold text-slate-900 dark:text-white">Curiow</h1>
          </button>
          <div className="flex items-center space-x-2 sm:space-x-4">
             {isLoggedIn && (
                 <>
                    <button
                        onClick={() => {
                            console.log('Saved button clicked');
                            onNavigate('saved');
                        }}
                        title="Liste Salvate"
                        className="p-2 rounded-full text-slate-600 dark:text-slate-300 hover:bg-slate-200 dark:hover:bg-slate-700 transition-colors"
                    >
                        <BookmarkSquareIcon className="w-6 h-6" />
                    </button>
                    <button
                        onClick={(e) => {
                            e.preventDefault();
                            e.stopPropagation();
                            onNavigate('profile');
                        }}
                        title="Profilo e Impostazioni"
                        className="p-2 rounded-full text-slate-600 dark:text-slate-300 hover:bg-slate-200 dark:hover:bg-slate-700 transition-colors"
                    >
                        <Cog6ToothIcon className="w-6 h-6" />
                    </button>
                 </>
             )}
             {isLoggedIn ? (
                <div className="relative" ref={menuRef}>
                  <button
                    onClick={() => setShowProfileMenu(!showProfileMenu)}
                    className="flex items-center space-x-2 text-sm font-medium text-slate-600 dark:text-slate-300 hover:text-indigo-600 dark:hover:text-indigo-400 transition-colors"
                  >
                    <UserCircleIcon className="w-6 h-6" />
                    <span className="hidden sm:inline">Profilo</span>
                    <ChevronDownIcon className={`w-4 h-4 transition-transform ${showProfileMenu ? 'rotate-180' : ''}`} />
                  </button>
                  {showProfileMenu && (
                    <div className="absolute right-0 z-20 mt-2 w-48 bg-white dark:bg-slate-800 rounded-md shadow-lg overflow-hidden">
                      <button
                        onClick={() => handleMenuItemClick('profile')}
                        className="block w-full text-left px-4 py-2 text-sm text-slate-700 dark:text-slate-300 hover:bg-slate-100 dark:hover:bg-slate-700"
                      >
                        Profilo
                      </button>
                      {permissions.canViewDashboard && (
                        <button
                          onClick={() => handleMenuItemClick('dashboard')}
                          className="block w-full text-left px-4 py-2 text-sm text-slate-700 dark:text-slate-300 hover:bg-slate-100 dark:hover:bg-slate-700"
                        >
                          Dashboard
                        </button>
                      )}
                      <div className="border-t border-slate-200 dark:border-slate-700"></div>
                      <button
                        onClick={onLogout}
                        className="block w-full text-left px-4 py-2 text-sm text-red-600 dark:text-red-400 hover:bg-red-50 dark:hover:bg-red-900"
                      >
                        Logout
                      </button>
                    </div>
                  )}
                </div>
             ) : (
                <button
                    onClick={onLogin}
                    className="flex items-center space-x-2 text-sm font-medium text-slate-600 dark:text-slate-300 hover:text-indigo-600 dark:hover:text-indigo-400 transition-colors"
                >
                    <UserCircleIcon className="w-6 h-6" />
                    <span className="hidden sm:inline">Login</span>
                </button>
             )}
          </div>
        </div>
        <div className="pb-4 overflow-x-auto">
            <nav className="flex items-center space-x-2 sm:space-x-3">
                <button onClick={() => onSelectFilter({ type: 'all' })} className={getButtonClass({ type: 'all' })}>Tutti</button>
                {isLoggedIn && <button onClick={() => onSelectFilter({ type: 'favorites' })} className={getButtonClass({ type: 'favorites' })}>Preferiti</button>}
                {selectedFilter.type === 'tag' && (
                  <>
                    <div className="border-l border-slate-300 dark:border-slate-600 h-6 mx-2"></div>
                    <span className="text-xs font-semibold text-slate-500 dark:text-slate-400 uppercase">Tag</span>
                    <button onClick={() => {}} className={getButtonClass(selectedFilter)}>
                      <TagIcon className="w-3 h-3 inline-block mr-1.5"/>{selectedFilter.value}
                    </button>
                  </>
                )}
                <div className="border-l border-slate-300 dark:border-slate-600 h-6 mx-2"></div>
                <span className="text-xs font-semibold text-slate-500 dark:text-slate-400 uppercase">Canali</span>
                {channels.map(channel => (
                    <button key={channel.id} onClick={() => onSelectFilter({type: 'channel', value: channel.id})} className={getButtonClass({type: 'channel', value: channel.id})}>
                        {channel.emoji} {channel.name}
                    </button>
                ))}
                <div className="border-l border-slate-300 dark:border-slate-600 h-6 mx-2"></div>
                <span className="text-xs font-semibold text-slate-500 dark:text-slate-400 uppercase">Argomenti</span>
                {TOPICS.map(topic => (
                    <button key={topic} onClick={() => onSelectFilter({type: 'topic', value: topic})} className={getButtonClass({type: 'topic', value: topic})}>
                        {topic.split(' ')[0]}
                    </button>
                ))}
            </nav>
        </div>
      </div>
    </header>
  );
};

export default Header;

import React, { useState, useRef, useEffect } from 'react';
import { TOPICS } from '../constants';
import { Topic, Channel, Filter, User } from '../types';
import { SparklesIcon, UserCircleIcon, BookmarkSquareIcon, Cog6ToothIcon, TagIcon, ChevronDownIcon } from './icons';
import { useUserPermissions } from '../services/roleService';

interface HeaderProps {
  isLoggedIn: boolean;
  user?: User | null;
  onLogin: () => void;
  onLogout: () => void;
  selectedFilter: Filter;
  onSelectFilter: (filter: Filter) => void;
  onNavigate: (view: 'feed' | 'saved' | 'profile' | 'dashboard' | 'topics') => void;
  channels: Channel[];
}

const Header: React.FC<HeaderProps> = ({ isLoggedIn, user, onLogin, onLogout, selectedFilter, onSelectFilter, onNavigate, channels }) => {
  const [showProfileMenu, setShowProfileMenu] = useState(false);
  const menuRef = useRef<HTMLDivElement>(null);

  const permissions = useUserPermissions(user);

  // Debug log per controllare i permessi
  useEffect(() => {
    console.log('Header - User:', user);
    console.log('Header - Permissions:', permissions);
  }, [user, permissions]);

  // Chiudi il menu quando si clicca fuori
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (menuRef.current && !menuRef.current.contains(event.target as Node)) {
        setShowProfileMenu(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const handleMenuItemClick = (view: 'profile' | 'dashboard' | 'topics') => {
    setShowProfileMenu(false);
    onNavigate(view);
  };

  const getButtonClass = (filter: Filter) => {
    const baseClass = 'px-3 py-1.5 text-sm font-semibold rounded-full transition-colors duration-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-slate-50 dark:focus:ring-offset-slate-900 focus:ring-indigo-500 whitespace-nowrap';

    if (selectedFilter.type === filter.type && ('value' in selectedFilter && 'value' in filter ? selectedFilter.value === filter.value : true)) {
      return `${baseClass} bg-indigo-600 text-white shadow-md`;
    }
    return `${baseClass} bg-white dark:bg-slate-800 text-slate-700 dark:text-slate-300 hover:bg-slate-100 dark:hover:bg-slate-700`;
  };

  return (
    <header className="sticky top-0 z-30 bg-slate-50/80 dark:bg-slate-900/80 backdrop-blur-lg border-b border-slate-200 dark:border-slate-700/50">
      <div className="max-w-5xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          <button onClick={() => { onSelectFilter({ type: 'all' }); onNavigate('feed'); }} className="flex items-center space-x-2">
            <SparklesIcon className="w-7 h-7 text-indigo-500" />
            <h1 className="text-xl font-bold text-slate-900 dark:text-white">Curiow</h1>
          </button>
          <div className="flex items-center space-x-2 sm:space-x-4">
             {isLoggedIn && (
                 <>
                    <button
                        onClick={() => {
                            console.log('Saved button clicked');
                            onNavigate('saved');
                        }}
                        title="Liste Salvate"
                        className="p-2 rounded-full text-slate-600 dark:text-slate-300 hover:bg-slate-200 dark:hover:bg-slate-700 transition-colors"
                    >
                        <BookmarkSquareIcon className="w-6 h-6" />
                    </button>
                    <button
                        onClick={(e) => {
                            e.preventDefault();
                            e.stopPropagation();
                            onNavigate('profile');
                        }}
                        title="Profilo e Impostazioni"
                        className="p-2 rounded-full text-slate-600 dark:text-slate-300 hover:bg-slate-200 dark:hover:bg-slate-700 transition-colors"
                    >
                        <Cog6ToothIcon className="w-6 h-6" />
                    </button>
                 </>
             )}
             {isLoggedIn ? (
                <div className="relative" ref={menuRef}>
                  <button
                    onClick={() => setShowProfileMenu(!showProfileMenu)}
                    className="flex items-center space-x-2 text-sm font-medium text-slate-600 dark:text-slate-300 hover:text-indigo-600 dark:hover:text-indigo-400 transition-colors"
                  >
                    <UserCircleIcon className="w-6 h-6" />
                    <span className="hidden sm:inline">Profilo</span>
                    <ChevronDownIcon className={`w-4 h-4 transition-transform ${showProfileMenu ? 'rotate-180' : ''}`} />
                  </button>
                  {showProfileMenu && (
                    <div className="absolute right-0 z-20 mt-2 w-48 bg-white dark:bg-slate-800 rounded-md shadow-lg overflow-hidden">
                      <button
                        onClick={() => handleMenuItemClick('profile')}
                        className="block w-full text-left px-4 py-2 text-sm text-slate-700 dark:text-slate-300 hover:bg-slate-100 dark:hover:bg-slate-700"
                      >
                        Profilo
                      </button>
                      {permissions.canViewDashboard && (
                        <button
                          onClick={() => handleMenuItemClick('dashboard')}
                          className="block w-full text-left px-4 py-2 text-sm text-slate-700 dark:text-slate-300 hover:bg-slate-100 dark:hover:bg-slate-700"
                        >
                          Dashboard
                        </button>
                      )}
                      <div className="border-t border-slate-200 dark:border-slate-700"></div>
                      <button
                        onClick={onLogout}
                        className="block w-full text-left px-4 py-2 text-sm text-red-600 dark:text-red-400 hover:bg-red-50 dark:hover:bg-red-900"
                      >
                        Logout
                      </button>
                    </div>
                  )}
                </div>
             ) : (
                <button
                    onClick={onLogin}
                    className="flex items-center space-x-2 text-sm font-medium text-slate-600 dark:text-slate-300 hover:text-indigo-600 dark:hover:text-indigo-400 transition-colors"
                >
                    <UserCircleIcon className="w-6 h-6" />
                    <span className="hidden sm:inline">Login</span>
                </button>
             )}
          </div>
        </div>
        <div className="pb-4 overflow-x-auto">
            <nav className="flex items-center space-x-2 sm:space-x-3">
                <button onClick={() => onSelectFilter({ type: 'all' })} className={getButtonClass({ type: 'all' })}>Tutti</button>
                {isLoggedIn && <button onClick={() => onSelectFilter({ type: 'favorites' })} className={getButtonClass({ type: 'favorites' })}>Preferiti</button>}
                {selectedFilter.type === 'tag' && (
                  <>
                    <div className="border-l border-slate-300 dark:border-slate-600 h-6 mx-2"></div>
                    <span className="text-xs font-semibold text-slate-500 dark:text-slate-400 uppercase">Tag</span>
                    <button onClick={() => {}} className={getButtonClass(selectedFilter)}>
                      <TagIcon className="w-3 h-3 inline-block mr-1.5"/>{selectedFilter.value}
                    </button>
                  </>
                )}
                <div className="border-l border-slate-300 dark:border-slate-600 h-6 mx-2"></div>
                <span className="text-xs font-semibold text-slate-500 dark:text-slate-400 uppercase">Canali</span>
                {channels.map(channel => (
                    <button key={channel.id} onClick={() => onSelectFilter({type: 'channel', value: channel.id})} className={getButtonClass({type: 'channel', value: channel.id})}>
                        {channel.emoji} {channel.name}
                    </button>
                ))}
                <div className="border-l border-slate-300 dark:border-slate-600 h-6 mx-2"></div>
                <span className="text-xs font-semibold text-slate-500 dark:text-slate-400 uppercase">Argomenti</span>
                {TOPICS.map(topic => (
                    <button key={topic} onClick={() => onSelectFilter({type: 'topic', value: topic})} className={getButtonClass({type: 'topic', value: topic})}>
                        {topic.split(' ')[0]}
                    </button>
                ))}
            </nav>
        </div>
      </div>
    </header>
  );
};

export default Header;

import React, { useState } from 'react';
import { ListWithItems, Gem } from '../types';
import { ChevronLeftIcon, PlusCircleIcon } from './icons';
import GemCard from './GemCard';

interface SavedViewProps {
    allGems: Gem[];
    allFavoriteIds: string[];
    savedLists: ListWithItems[];
    onUpdateLists: (lists: ListWithItems[]) => void;
    onSelectGem: (gemId: string) => void;
    onToggleFavorite: (gemId: string) => void;
    onLoginRequest: () => void;
    onBack: () => void;

const SavedView: React.FC<SavedViewProps> = ({ allGems, allFavoriteIds, savedLists, onUpdateLists, onSelectGem, onToggleFavorite, onLoginRequest, onBack }) => {
const SavedView: React.FC<SavedViewProps> = ({ allGems, allFavoriteIds, savedLists, onUpdateLists, onSelectGem, onToggleFavorite, onLoginRequest, onBack }) => {
    const userLists = savedLists.filter(l => l.id !== 'default');
    const [selectedListId, setSelectedListId] = useState<string>('all');

    const handleCreateList = (e: React.FormEvent) => {
    const handleCreateList = (e: React.FormEvent) => {
        if (newListName.trim()) {
            // Ora utilizziamo il tipo ListWithItems invece di SavedList
            // Ora utilizziamo il tipo ListWithItems invece di SavedList
            const newList: ListWithItems = {
                id: self.crypto.randomUUID(),
                name: newListName.trim(),
                isPublic: false,
                createdBy: 'current-user', // Questo sarà gestito dall'App.tsx
                createdAt: new Date(),
                updatedAt: new Date(),
                gemIds: [],
                itemCount: 0,
                userRole: 'owner'
            };
            onUpdateLists([newList, ...savedLists]);
            setNewListName('');
                gemIds: [],
                itemCount: 0,
                userRole: 'owner'
            };
            onUpdateLists([newList, ...savedLists]);
            setNewListName('');
        }
    };

    const getGemsForList = (): Gem[] => {
        if (selectedListId === 'all') {
            return allGems.filter(gem => allFavoriteIds.includes(gem.id));
        }
        const list = savedLists.find(l => l.id === selectedListId);
        return list ? allGems.filter(gem => list.gemIds.includes(gem.id)) : [];
    };

    const getButtonClass = (id: string) => {
        const baseClass = 'px-4 py-2 text-sm font-semibold rounded-lg transition-colors duration-200 text-left';
        if (selectedListId === id) {
            return `${baseClass} bg-indigo-100 dark:bg-indigo-900/50 text-indigo-700 dark:text-indigo-300`;
        }
        return `${baseClass} text-slate-700 dark:text-slate-300 hover:bg-slate-100 dark:hover:bg-slate-800`;
    };

    const gemsInView = getGemsForList();

    return (
        <div className="max-w-5xl mx-auto">
            <header className="sticky top-0 z-10 flex items-center p-2 sm:p-4 bg-slate-50/80 dark:bg-slate-900/80 backdrop-blur-lg border-b border-slate-200 dark:border-slate-700/50">
                <button
                    onClick={onBack}
                    className="p-2 rounded-full text-slate-600 dark:text-slate-300 hover:bg-slate-200 dark:hover:bg-slate-700 transition-colors"
                    aria-label="Torna indietro"
                >
                    <ChevronLeftIcon className="w-6 h-6" />
                </button>
                <h1 className="ml-4 text-xl font-bold text-slate-900 dark:text-white">Le tue Liste</h1>
            </header>

            <div className="flex flex-col md:flex-row">
                <aside className="w-full md:w-64 lg:w-72 p-4 border-b md:border-b-0 md:border-r border-slate-200 dark:border-slate-700">
                    <h2 className="text-sm font-semibold text-slate-500 dark:text-slate-400 uppercase tracking-wider">Liste</h2>
                    <nav className="mt-4 flex flex-col space-y-1">
                        <button onClick={() => setSelectedListId('all')} className={getButtonClass('all')}>
                            Tutti i Preferiti ({allFavoriteIds.length})
                        </button>
                        {userLists.map(list => (
                             <button key={list.id} onClick={() => setSelectedListId(list.id)} className={getButtonClass(list.id)}>
                                {list.name} ({list.gemIds.length})
                            </button>
                        ))}
                    </nav>

                    <form onSubmit={handleCreateList} className="mt-6">
                         <h2 className="text-sm font-semibold text-slate-500 dark:text-slate-400 uppercase tracking-wider">Nuova Lista</h2>
                         <div className="mt-2 flex space-x-2">
                            <input
                                type="text"
                                value={newListName}
                                onChange={(e) => setNewListName(e.target.value)}
                                placeholder="Nome della lista..."
                                className="flex-grow bg-white dark:bg-slate-800 border border-slate-300 dark:border-slate-600 rounded-md py-1.5 px-3 text-sm text-slate-900 dark:text-white placeholder-slate-500 dark:placeholder-slate-400 focus:outline-none focus:ring-1 focus:ring-indigo-500"
                            />
                            <button type="submit" className="p-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 disabled:bg-indigo-400" disabled={!newListName.trim()}>
                                <PlusCircleIcon className="w-5 h-5" />
                            </button>
                         </div>
                    </form>
                </aside>

                <main className="flex-1 p-4 sm:p-6 lg:p-8">
                    {gemsInView.length > 0 ? (
                        <div className="grid grid-cols-1 sm:grid-cols-2 gap-8 max-w-xl mx-auto">
                           {gemsInView.map(gem => (
                                <GemCard
                                    key={gem.id}
                                    gem={gem}
                                    isLoggedIn={true}
                                    isFavorite={allFavoriteIds.includes(gem.id)}
                                    onSaveRequest={onToggleFavorite} /* Using onToggleFavorite here to just remove from all lists */
                                    onSelect={onSelectGem}
                                    onLoginRequest={onLoginRequest}
                                />
                            ))}
                        </div>
                    ) : (
                        <div className="text-center pt-20 text-slate-500 dark:text-slate-400">
                            <h3 className="text-xl font-semibold">Nessuna gemma in questa lista</h3>
                            <p className="mt-2 max-w-md mx-auto">
                                {selectedListId === 'all' 
                                    ? "Salva le tue gemme preferite cliccando sul cuore. Le troverai tutte qui."
                                    : "Aggiungi gemme a questa lista dalla loro pagina di dettaglio o dal popup di salvataggio."
                                }
                            </p>
                        </div>
                    )}
                </main>
            </div>
        </div>
    );
};

export default SavedView;

import React, { useState } from 'react';
import { ListWithItems, Gem } from '../types';
import { ChevronLeftIcon, PlusCircleIcon } from './icons';
import GemCard from './GemCard';

interface SavedViewProps {
    allGems: Gem[];
    allFavoriteIds: string[];
    savedLists: ListWithItems[];
    onUpdateLists: (lists: ListWithItems[]) => void;
    onSelectGem: (gemId: string) => void;
    onToggleFavorite: (gemId: string) => void;
    onLoginRequest: () => void;
    onBack: () => void;
    onCreateList: (listName: string) => Promise<void>; // Nuova prop per creare liste
}

const SavedView: React.FC<SavedViewProps> = ({ allGems, allFavoriteIds, savedLists, onUpdateLists, onSelectGem, onToggleFavorite, onLoginRequest, onBack, onCreateList }) => {
    const [newListName, setNewListName] = useState('');
    const userLists = savedLists.filter(l => l.id !== 'default');
    const [selectedListId, setSelectedListId] = useState<string>('all');

    const handleCreateList = async (e: React.FormEvent) => {
        e.preventDefault();
        if (newListName.trim()) {
            try {
                await onCreateList(newListName.trim()); // Usa solo il servizio
                setNewListName('');
            } catch (error) {
                console.error('Error creating list:', error);
                alert('Errore nella creazione della lista');
            }
        }
    };

    const getGemsForList = (): Gem[] => {
        if (selectedListId === 'all') {
            return allGems.filter(gem => allFavoriteIds.includes(gem.id));
        }
        const list = savedLists.find(l => l.id === selectedListId);
        return list ? allGems.filter(gem => list.gemIds.includes(gem.id)) : [];
    };

    const getButtonClass = (id: string) => {
        const baseClass = 'px-4 py-2 text-sm font-semibold rounded-lg transition-colors duration-200 text-left';
        if (selectedListId === id) {
            return `${baseClass} bg-indigo-100 dark:bg-indigo-900/50 text-indigo-700 dark:text-indigo-300`;
        }
        return `${baseClass} text-slate-700 dark:text-slate-300 hover:bg-slate-100 dark:hover:bg-slate-800`;
    };

    const gemsInView = getGemsForList();

    return (
        <div className="max-w-5xl mx-auto">
            <header className="sticky top-0 z-10 flex items-center p-2 sm:p-4 bg-slate-50/80 dark:bg-slate-900/80 backdrop-blur-lg border-b border-slate-200 dark:border-slate-700/50">
                <button
                    onClick={onBack}
                    className="p-2 rounded-full text-slate-600 dark:text-slate-300 hover:bg-slate-200 dark:hover:bg-slate-700 transition-colors"
                    aria-label="Torna indietro"
                >
                    <ChevronLeftIcon className="w-6 h-6" />
                </button>
                <h1 className="ml-4 text-xl font-bold text-slate-900 dark:text-white">Le tue Liste</h1>
            </header>

            <div className="flex flex-col md:flex-row">
                <aside className="w-full md:w-64 lg:w-72 p-4 border-b md:border-b-0 md:border-r border-slate-200 dark:border-slate-700">
                    <h2 className="text-sm font-semibold text-slate-500 dark:text-slate-400 uppercase tracking-wider">Liste</h2>
                    <nav className="mt-4 flex flex-col space-y-1">
                        <button onClick={() => setSelectedListId('all')} className={getButtonClass('all')}>
                            Tutti i Preferiti ({allFavoriteIds.length})
                        </button>
                        {userLists.map(list => (
                             <button key={list.id} onClick={() => setSelectedListId(list.id)} className={getButtonClass(list.id)}>
                                {list.name} ({list.gemIds.length})
                            </button>
                        ))}
                    </nav>

                    <form onSubmit={handleCreateList} className="mt-6">
                         <h2 className="text-sm font-semibold text-slate-500 dark:text-slate-400 uppercase tracking-wider">Nuova Lista</h2>
                         <div className="mt-2 flex space-x-2">
                            <input
                                type="text"
                                value={newListName}
                                onChange={(e) => setNewListName(e.target.value)}
                                placeholder="Nome della lista..."
                                className="flex-grow bg-white dark:bg-slate-800 border border-slate-300 dark:border-slate-600 rounded-md py-1.5 px-3 text-sm text-slate-900 dark:text-white placeholder-slate-500 dark:placeholder-slate-400 focus:outline-none focus:ring-1 focus:ring-indigo-500"
                            />
                            <button type="submit" className="p-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 disabled:bg-indigo-400" disabled={!newListName.trim()}>
                                <PlusCircleIcon className="w-5 h-5" />
                            </button>
                         </div>
                    </form>
                </aside>

                <main className="flex-1 p-4 sm:p-6 lg:p-8">
                    {gemsInView.length > 0 ? (
                        <div className="grid grid-cols-1 sm:grid-cols-2 gap-8 max-w-xl mx-auto">
                           {gemsInView.map(gem => (
                                <GemCard
                                    key={gem.id}
                                    gem={gem}
                                    isLoggedIn={true}
                                    isFavorite={allFavoriteIds.includes(gem.id)}
                                    onSaveRequest={onToggleFavorite} /* Using onToggleFavorite here to just remove from all lists */
                                    onSelect={onSelectGem}
                                    onLoginRequest={onLoginRequest}
                                />
                            ))}
                        </div>
                    ) : (
                        <div className="text-center pt-20 text-slate-500 dark:text-slate-400">
                            <h3 className="text-xl font-semibold">Nessuna gemma in questa lista</h3>
                            <p className="mt-2 max-w-md mx-auto">
                                {selectedListId === 'all' 
                                    ? "Salva le tue gemme preferite cliccando sul cuore. Le troverai tutte qui."
                                    : "Aggiungi gemme a questa lista dalla loro pagina di dettaglio o dal popup di salvataggio."
                                }
                            </p>
                        </div>
                    )}
                </main>
            </div>
        </div>
    );
};

export default SavedView;

import React, { useState } from 'react';
import { User, UserRole } from '../../types';
import { useUserPermissions } from '../../services/roleService';
import ProfileView from '../ProfileView';
import UserManagement from './UserManagement';
import TopicManagement from './TopicManagement';

interface AdminDashboardProps {
  currentUser: User & { id: string } | null;
  onClose: () => void;
}

type DashboardView = 'profile' | 'users' | 'topics';

const AdminDashboard: React.FC<AdminDashboardProps> = ({ currentUser, onClose }) => {
  const [activeView, setActiveView] = useState<DashboardView>('profile');
  const permissions = useUserPermissions(currentUser);

  const menuItems = [
    {
      id: 'profile' as DashboardView,
      label: 'Il Mio Profilo',
      icon: '',
      description: 'Visualizza e modifica il tuo profilo',
      allowedForAll: true,
    },
    {
      id: 'topics' as DashboardView,
      label: 'Gestione Argomenti',
      icon: '',
      description: 'Crea e gestisci gli argomenti per le gemme',
      allowedForAll: false,
      requiresAdmin: true,
    },
    {
      id: 'users' as DashboardView,
      label: 'Gestione Utenti',
      icon: '',
      description: 'Gestisci ruoli e permessi degli utenti',
      allowedForAll: false,
      requiresAdmin: true,
    },
  ];

  const visibleMenuItems = menuItems.filter(item =>
    item.allowedForAll || (item.requiresAdmin && permissions.isAdmin)
  );

  const renderActiveView = () => {
    if (!currentUser) return null;

    // Converti currentUser nel formato che si aspetta ProfileView
    const userForProfileView: User = {
      firstName: currentUser.firstName,
      lastName: currentUser.lastName,
      email: currentUser.email,
      role: currentUser.role,
      permissions: currentUser.permissions,
      createdAt: currentUser.createdAt,
      lastLoginAt: currentUser.lastLoginAt
    };

    switch (activeView) {
      case 'profile':
        return (
          <ProfileView
            user={userForProfileView}
            onUpdateUser={(updatedUser) => {
              // TODO: Implementare aggiornamento utente nel dashboard
              console.log('Aggiornamento utente nel dashboard:', updatedUser);
            }}
            onBack={() => setActiveView('profile')} // Rimane sulla stessa vista
            onNavigate={() => {}} // Non naviga fuori dal dashboard
          />
        );
      case 'users':
        return permissions.isAdmin ? <UserManagement currentUser={currentUser} /> : null;
      case 'topics':
        return permissions.isAdmin ? (
          <TopicManagement
            currentUser={currentUser}
            onBack={() => setActiveView('topics')} // Rimane nel dashboard
          />
        ) : null;
      default:
        return null;
    }
  };

  const getRoleBadgeColor = (role: UserRole) => {
    switch (role) {
      case UserRole.ADMIN: return 'bg-red-100 text-red-800';
      case UserRole.MODERATOR: return 'bg-yellow-100 text-yellow-800';
      case UserRole.USER: return 'bg-green-100 text-green-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <div className="fixed inset-0 bg-gray-50 z-50 overflow-hidden">
      <div className="h-full flex">
        {/* Sidebar */}
        <div className="w-80 bg-white shadow-lg border-r border-gray-200 flex flex-col">
          {/* Header */}
          <div className="p-6 border-b border-gray-200">
            <div className="flex items-center justify-between mb-4">
              <h1 className="text-2xl font-bold text-gray-900">Dashboard</h1>
              <button
                onClick={onClose}
                className="p-2 text-gray-400 hover:text-gray-600 rounded-lg hover:bg-gray-100"
              >
                ✕
              </button>
            </div>

            {currentUser && (
              <div className="flex items-center space-x-3">
                <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center">
                  <span className="text-blue-600 font-semibold text-lg">
                    {currentUser.firstName?.[0]}{currentUser.lastName?.[0]}
                  </span>
                </div>
                <div>
                  <p className="font-medium text-gray-900">
                    {currentUser.firstName} {currentUser.lastName}
                  </p>
                  <div className="flex items-center space-x-2">
                    <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${getRoleBadgeColor(currentUser.role)}`}>
                      {currentUser.role}
                    </span>
                  </div>
                </div>
              </div>
            )}
          </div>

          {/* Menu */}
          <nav className="flex-1 p-4">
            <ul className="space-y-2">
              {visibleMenuItems.map((item) => (
                <li key={item.id}>
                  <button
                    onClick={() => setActiveView(item.id)}
                    className={`w-full text-left p-4 rounded-lg transition-colors ${
                      activeView === item.id
                        ? 'bg-blue-50 text-blue-700 border border-blue-200'
                        : 'text-gray-700 hover:bg-gray-50 hover:text-gray-900'
                    }`}
                  >
                    <div className="flex items-center space-x-3">
                      <span className="text-2xl">{item.icon}</span>
                      <div>
                        <p className="font-medium">{item.label}</p>
                        <p className="text-sm text-gray-500">{item.description}</p>
                      </div>
                    </div>
                  </button>
                </li>
              ))}
            </ul>
          </nav>

          {/* Footer */}
          <div className="p-4 border-t border-gray-200">
            <p className="text-xs text-gray-500 text-center">
              Curiow Dashboard v1.0
            </p>
          </div>
        </div>

        {/* Main Content */}
        <div className="flex-1 overflow-auto">
          {renderActiveView()}
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;

import React, { useState } from 'react';
import { User, UserRole } from '../../types';
import { useUserPermissions } from '../../services/roleService';
import ProfileView from '../ProfileView';
import UserManagement from './UserManagement';
import TopicManagement from './TopicManagement';
import FeedbackManagement from './FeedbackManagement';

interface AdminDashboardProps {
  currentUser: User & { id: string } | null;
  onClose: () => void;
}

type DashboardView = 'profile' | 'users' | 'topics' | 'feedback';

const AdminDashboard: React.FC<AdminDashboardProps> = ({ currentUser, onClose }) => {
  const [activeView, setActiveView] = useState<DashboardView>('profile');
  const permissions = useUserPermissions(currentUser);

  const menuItems = [
    {
      id: 'profile' as DashboardView,
      label: 'Il Mio Profilo',
