  WELLBEING = "Benessere & Sviluppo Personale",
  ECONOMY = "Economia, Finanza & Società",
}

export enum UserRole {
  USER = "user",
  MODERATOR = "moderator",
  ADMIN = "admin",
  BETATESTER = "betatester"
}

