  isFavorite: boolean;
  onSaveRequest: (gemId: string) => void;
  onSelect: (gemId: string) => void;
  onLoginRequest: () => void;
  onView?: () => void; // Nuova prop per tracciare le visualizzazioni
}

const GemCard: React.FC<GemCardProps> = ({ gem, isLoggedIn, isFavorite, onSaveRequest, onSelect, onLoginRequest, onView }) => {

  const handleCardClick = () => {
    if (!isLoggedIn) {
      onLoginRequest();
    } else {
      onSelect(gem.id);
    }
  };

  const handleFavoriteClick = (e: React.MouseEvent) => {
    e.stopPropagation();
    if (!isLoggedIn) {
        onLoginRequest();
    } else {
        onSaveRequest(gem.id);
    }
  };

  const handleShareClick = (e: React.MouseEvent) => {
    e.stopPropagation();
    if (!isLoggedIn) {
        onLoginRequest();
    } else {
        navigator.clipboard.writeText(`Scopri questa gemma di conoscenza: "${gem.title}" su Curiow!`);
        alert("Contenuto copiato negli appunti!");
    }
  };

  React.useEffect(() => {
    if (onView) {
      onView();
