        const baseClass = 'px-4 py-2 text-sm font-semibold rounded-lg transition-colors duration-200 text-left';
        if (selectedListId === id) {
            return `${baseClass} bg-indigo-100 dark:bg-indigo-900/50 text-indigo-700 dark:text-indigo-300`;
        }
        return `${baseClass} text-slate-700 dark:text-slate-300 hover:bg-slate-100 dark:hover:bg-slate-800`;
    };

    const gemsInView = getGemsForList();

    return (
        <div className="max-w-5xl mx-auto">
            <header className="sticky top-0 z-10 flex items-center p-2 sm:p-4 bg-slate-50/80 dark:bg-slate-900/80 backdrop-blur-lg border-b border-slate-200 dark:border-slate-700/50">
                <button
                    onClick={onBack}
                    className="p-2 rounded-full text-slate-600 dark:text-slate-300 hover:bg-slate-200 dark:hover:bg-slate-700 transition-colors"
                    aria-label="Torna indietro"
                >
                    <ChevronLeftIcon className="w-6 h-6" />
                </button>
                <h1 className="ml-4 text-xl font-bold text-slate-900 dark:text-white">Le tue Liste</h1>
            </header>

            <div className="flex flex-col md:flex-row">
                <aside className="w-full md:w-64 lg:w-72 p-4 border-b md:border-b-0 md:border-r border-slate-200 dark:border-slate-700">
                    <h2 className="text-sm font-semibold text-slate-500 dark:text-slate-400 uppercase tracking-wider">Liste</h2>
                    <nav className="mt-4 flex flex-col space-y-1">
                        <button onClick={() => setSelectedListId('all')} className={getButtonClass('all')}>
                            Tutti i Preferiti ({allFavoriteIds.length})
                        </button>
                        {userLists.map(list => (
                             <button key={list.id} onClick={() => setSelectedListId(list.id)} className={getButtonClass(list.id)}>
                                {list.name} ({list.gemIds.length})
                            </button>
                        ))}
                    </nav>

                    <form onSubmit={handleCreateList} className="mt-6">
                         <h2 className="text-sm font-semibold text-slate-500 dark:text-slate-400 uppercase tracking-wider">Nuova Lista</h2>
                         <div className="mt-2 flex space-x-2">
                            <input
                                type="text"
                                value={newListName}
                                onChange={(e) => setNewListName(e.target.value)}
                                placeholder="Nome della lista..."
                                className="flex-grow bg-white dark:bg-slate-800 border border-slate-300 dark:border-slate-600 rounded-md py-1.5 px-3 text-sm text-slate-900 dark:text-white placeholder-slate-500 dark:placeholder-slate-400 focus:outline-none focus:ring-1 focus:ring-indigo-500"
                            />
                            <button type="submit" className="p-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 disabled:bg-indigo-400" disabled={!newListName.trim()}>
                                <PlusCircleIcon className="w-5 h-5" />
                            </button>
                         </div>
                    </form>
                </aside>

                <main className="flex-1 p-4 sm:p-6 lg:p-8">
                    {gemsInView.length > 0 ? (
                        <div className="grid grid-cols-1 sm:grid-cols-2 gap-8 max-w-xl mx-auto">
                           {gemsInView.map(gem => (
                                <GemCard
                                    key={gem.id}
                                    gem={gem}
                                    isLoggedIn={true}
                                    isFavorite={allFavoriteIds.includes(gem.id)}
                                    onSaveRequest={onToggleFavorite} /* Using onToggleFavorite here to just remove from all lists */
                                    onSelect={onSelectGem}
                                    onLoginRequest={onLoginRequest}
                                />
                            ))}
                        </div>
                    ) : (
                        <div className="text-center pt-20 text-slate-500 dark:text-slate-400">
                            <h3 className="text-xl font-semibold">Nessuna gemma in questa lista</h3>
                            <p className="mt-2 max-w-md mx-auto">
                                {selectedListId === 'all' 
                                    ? "Salva le tue gemme preferite cliccando sul cuore. Le troverai tutte qui."
                                    : "Aggiungi gemme a questa lista dalla loro pagina di dettaglio o dal popup di salvataggio."
                                }
                            </p>
                        </div>
                    )}
                </main>
            </div>
        </div>
    );
};

export default SavedView;

import React, { useState } from 'react';
import { ListWithItems, Gem } from '../types';
import { ChevronLeftIcon, PlusCircleIcon } from './icons';
import GemCard from './GemCard';

interface SavedViewProps {
    allGems: Gem[];
    allFavoriteIds: string[];
    savedLists: ListWithItems[];
    onUpdateLists: (lists: ListWithItems[]) => void;
    onSelectGem: (gemId: string) => void;
    onToggleFavorite: (gemId: string) => void;
    onLoginRequest: () => void;
    onBack: () => void;
    onCreateList: (listName: string) => Promise<void>; // Nuova prop per creare liste
}

const SavedView: React.FC<SavedViewProps> = ({ allGems, allFavoriteIds, savedLists, onUpdateLists, onSelectGem, onToggleFavorite, onLoginRequest, onBack, onCreateList }) => {
    const [newListName, setNewListName] = useState('');
    const userLists = savedLists.filter(l => l.id !== 'default');
    const [selectedListId, setSelectedListId] = useState<string>('all');

    const handleCreateList = async (e: React.FormEvent) => {
        e.preventDefault();
        if (newListName.trim()) {
            try {
                await onCreateList(newListName.trim()); // Usa solo il servizio
                setNewListName('');
            } catch (error) {
                console.error('Error creating list:', error);
                alert('Errore nella creazione della lista');
            }
        }
    };

    const getGemsForList = (): Gem[] => {
        if (selectedListId === 'all') {
            return allGems.filter(gem => allFavoriteIds.includes(gem.id));
        }
        const list = savedLists.find(l => l.id === selectedListId);
        return list ? allGems.filter(gem => list.gemIds.includes(gem.id)) : [];
    };

    const getButtonClass = (id: string) => {
        const baseClass = 'px-4 py-2 text-sm font-semibold rounded-lg transition-colors duration-200 text-left';
        if (selectedListId === id) {
            return `${baseClass} bg-indigo-100 dark:bg-indigo-900/50 text-indigo-700 dark:text-indigo-300`;
        }
        return `${baseClass} text-slate-700 dark:text-slate-300 hover:bg-slate-100 dark:hover:bg-slate-800`;
    };

    const gemsInView = getGemsForList();

    return (
        <div className="max-w-5xl mx-auto">
            <header className="sticky top-0 z-10 flex items-center p-2 sm:p-4 bg-slate-50/80 dark:bg-slate-900/80 backdrop-blur-lg border-b border-slate-200 dark:border-slate-700/50">
                <button
                    onClick={onBack}
                    className="p-2 rounded-full text-slate-600 dark:text-slate-300 hover:bg-slate-200 dark:hover:bg-slate-700 transition-colors"
                    aria-label="Torna indietro"
                >
                    <ChevronLeftIcon className="w-6 h-6" />
                </button>
                <h1 className="ml-4 text-xl font-bold text-slate-900 dark:text-white">Le tue Liste</h1>
            </header>

            <div className="flex flex-col md:flex-row">
                <aside className="w-full md:w-64 lg:w-72 p-4 border-b md:border-b-0 md:border-r border-slate-200 dark:border-slate-700">
                    <h2 className="text-sm font-semibold text-slate-500 dark:text-slate-400 uppercase tracking-wider">Liste</h2>
                    <nav className="mt-4 flex flex-col space-y-1">
                        <button onClick={() => setSelectedListId('all')} className={getButtonClass('all')}>
                            Tutti i Preferiti ({allFavoriteIds.length})
                        </button>
                        {userLists.map(list => (
                             <button key={list.id} onClick={() => setSelectedListId(list.id)} className={getButtonClass(list.id)}>
                                {list.name} ({list.gemIds.length})
                            </button>
                        ))}
                    </nav>

                    <form onSubmit={handleCreateList} className="mt-6">
                         <h2 className="text-sm font-semibold text-slate-500 dark:text-slate-400 uppercase tracking-wider">Nuova Lista</h2>
                         <div className="mt-2 flex space-x-2">
                            <input
                                type="text"
                                value={newListName}
                                onChange={(e) => setNewListName(e.target.value)}
                                placeholder="Nome della lista..."
                                className="flex-grow bg-white dark:bg-slate-800 border border-slate-300 dark:border-slate-600 rounded-md py-1.5 px-3 text-sm text-slate-900 dark:text-white placeholder-slate-500 dark:placeholder-slate-400 focus:outline-none focus:ring-1 focus:ring-indigo-500"
                            />
                            <button type="submit" className="p-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 disabled:bg-indigo-400" disabled={!newListName.trim()}>
                                <PlusCircleIcon className="w-5 h-5" />
                            </button>
                         </div>
                    </form>
                </aside>

                <main className="flex-1 p-4 sm:p-6 lg:p-8">
                    {gemsInView.length > 0 ? (
                        <div className="grid grid-cols-1 sm:grid-cols-2 gap-8 max-w-xl mx-auto">
                           {gemsInView.map(gem => (
                                <GemCard
                                    key={gem.id}
                                    gem={gem}
                                    isLoggedIn={true}
                                    isFavorite={allFavoriteIds.includes(gem.id)}
                                    onSaveRequest={onToggleFavorite} /* Using onToggleFavorite here to just remove from all lists */
                                    onSelect={onSelectGem}
                                    onLoginRequest={onLoginRequest}
                                />
                            ))}
                        </div>
                    ) : (
                        <div className="text-center pt-20 text-slate-500 dark:text-slate-400">
                            <h3 className="text-xl font-semibold">Nessuna gemma in questa lista</h3>
                            <p className="mt-2 max-w-md mx-auto">
                                {selectedListId === 'all' 
                                    ? "Salva le tue gemme preferite cliccando sul cuore. Le troverai tutte qui."
                                    : "Aggiungi gemme a questa lista dalla loro pagina di dettaglio o dal popup di salvataggio."
                                }
                            </p>
                        </div>
                    )}
                </main>
            </div>
        </div>
    );
};

export default SavedView;

import React, { useState, useEffect } from 'react';
import { BetaFeedback } from '../../types';
import * as feedbackService from '../../services/feedbackService';
// Modal di conferma per eliminazione
interface ConfirmationModalProps {
  isOpen: boolean;
  onClose: () => void;
  onConfirm: () => void;
  title: string;
  message: string;
  action: string;
}

const ConfirmationModal: React.FC<ConfirmationModalProps> = ({
  isOpen, onClose, onConfirm, title, message, action
}) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg max-w-md w-full p-6">
        <h3 className="text-lg font-semibold text-gray-900 mb-2">{title}</h3>
        <p className="text-gray-600 mb-6">{message}</p>
        <div className="flex justify-end space-x-3">
          <button
            onClick={onClose}
            className="px-4 py-2 text-gray-700 bg-gray-100 rounded-md hover:bg-gray-200 transition-colors"
          >
            Annulla
          </button>
          <button
            onClick={() => {
              onConfirm();
              onClose();
            }}
            className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 transition-colors"
          >
            {action}
          </button>
        </div>
      </div>
    </div>
  );
};

const FeedbackManagement: React.FC<FeedbackManagementProps> = ({ onBack }) => {
  const [feedbacks, setFeedbacks] = useState<BetaFeedback[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');

  // Filtri
  const [selectedSection, setSelectedSection] = useState('');
  const [selectedUser, setSelectedUser] = useState('');
  const [selectedStatus, setSelectedStatus] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');

  // Opzioni per i filtri
  const [sections, setSections] = useState<string[]>([]);
  const [users, setUsers] = useState<Array<{userId: string, userEmail: string}>>([]);

  // Modal di conferma
  const [confirmModal, setConfirmModal] = useState<{
    isOpen: boolean;
    title: string;
    message: string;
    action: string;
    onConfirm: () => void;
  }>({
    isOpen: false,
    title: '',
    message: '',
    action: '',
    onConfirm: () => {}
  });

  useEffect(() => {
    loadInitialData();
  }, []);

  useEffect(() => {
    loadFeedbacks();
  }, [selectedSection, selectedUser, selectedStatus, startDate, endDate]);

  const loadInitialData = async () => {
    try {
      const [sectionsData, usersData] = await Promise.all([
        feedbackService.getUniqueSections(),
        feedbackService.getUniqueUsers()
      ]);
      setSections(sectionsData);
      setUsers(usersData);
    } catch (err) {
      console.error('Errore nel caricamento dei dati iniziali:', err);
    }
  };

  const loadFeedbacks = async () => {
    setIsLoading(true);
    setError('');

    try {
      const filters: any = {};

      if (selectedSection) filters.section = selectedSection;
      if (selectedUser) filters.userId = selectedUser;
      if (selectedStatus) filters.status = selectedStatus;
      if (startDate) filters.startDate = new Date(startDate);
      if (endDate) filters.endDate = new Date(endDate);

      const data = await feedbackService.fetchFeedbacks(filters);
      setFeedbacks(data);
    } catch (err) {
      setError('Errore nel caricamento dei feedback');
      console.error('Errore nel caricamento dei feedback:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const clearFilters = () => {
    setSelectedSection('');
    setSelectedUser('');
    setSelectedStatus('');
    setStartDate('');
    setEndDate('');
  };

  const handleStatusChange = async (feedbackId: string, newStatus: 'inviato' | 'letto' | 'risolto') => {
    try {
      await feedbackService.updateFeedbackStatus(feedbackId, newStatus);
      await loadFeedbacks(); // Ricarica i feedback
    } catch (err) {
      console.error('Errore nell\'aggiornamento dello stato:', err);
      alert('Errore nell\'aggiornamento dello stato');
    }
  };

  const handleDelete = (feedbackId: string) => {
    setConfirmModal({
      isOpen: true,
      title: 'Elimina Feedback',
      message: 'Sei sicuro di voler eliminare questo feedback? Questa azione non può essere annullata.',
      action: 'Elimina',
      onConfirm: async () => {
        try {
          await feedbackService.deleteFeedback(feedbackId);
          await loadFeedbacks(); // Ricarica i feedback
        } catch (err) {
          console.error('Errore nell\'eliminazione del feedback:', err);
          alert('Errore nell\'eliminazione del feedback');
        }
      }
    });
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'inviato':
        return 'bg-blue-100 text-blue-800';
      case 'letto':
        return 'bg-yellow-100 text-yellow-800';
      case 'risolto':
        return 'bg-green-100 text-green-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const formatDate = (date: Date) => {
    return new Intl.DateTimeFormat('it-IT', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    }).format(date);
  };

  return (
    <div className="p-8 h-full bg-gray-50">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        {/* Header */}
        {/* Header */}
        {/* Header */}
        {/* Header */}
        {/* Header */}
                <option value="">Tutti gli utenti</option>
              <label htmlFor="status-filter" className="block text-sm font-medium text-gray-700 mb-2">
                Stato
              </label>
              <select
                id="status-filter"
                value={selectedStatus}
                onChange={(e) => setSelectedStatus(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              >
                <option value="">Tutti gli stati</option>
                <option value="inviato">Inviato</option>
                <option value="letto">Letto</option>
                <option value="risolto">Risolto</option>
              </select>
            </div>

            <div>
              <label htmlFor="start-date" className="block text-sm font-medium text-gray-700 mb-2">
                Data inizio
              </label>
              <input
                id="start-date"
                type="date"
                value={startDate}
                onChange={(e) => setStartDate(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>

            <div>
              <label htmlFor="end-date" className="block text-sm font-medium text-gray-700 mb-2">
                Data fine
              </label>
              <input
                id="end-date"
                type="date"
                value={endDate}
                onChange={(e) => setEndDate(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>
          </div>

          <div className="mt-4">
            <button
              onClick={clearFilters}
              className="px-4 py-2 text-gray-600 bg-gray-100 rounded-lg hover:bg-gray-200 transition-colors"
            >
              Cancella filtri
            </button>
          </div>
        </div>

        {/* Lista feedback */}
        {error && (
          <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-lg mb-6">
            {error}
          </div>
        )}

        {isLoading ? (
          <div className="flex justify-center py-8">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
          </div>
        ) : (
          <div className="space-y-4">
            {feedbacks.length === 0 ? (
              <div className="text-center py-8 text-gray-500 bg-white rounded-lg border border-gray-200">
                Nessun feedback trovato con i filtri selezionati
              </div>
            ) : (
              feedbacks.map(feedback => (
                <div key={feedback.id} className="bg-white border border-gray-200 rounded-lg p-6">
                  <div className="flex justify-between items-start mb-4">
                    <div className="flex-1">
                      <div className="flex items-center space-x-4 mb-2">
                        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                          {feedback.section}
                        </span>
                        <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getStatusBadge(feedback.status)}`}>
                          {feedback.status}
                        </span>
                      </div>
                      <div className="flex items-center space-x-4 text-sm text-gray-600 mb-2">
                        <span className="font-medium">{feedback.userName}</span>
                        <span>{feedback.userEmail}</span>
                      </div>
                      <div className="text-sm text-gray-500">
                        Creato: {formatDate(feedback.createdAt)}
                        {feedback.updatedAt && feedback.updatedAt.getTime() !== feedback.createdAt.getTime() && (
                          <span className="ml-4">
                            Aggiornato: {formatDate(feedback.updatedAt)}
                          </span>
                        )}
                      </div>
                    </div>
                    <div className="flex items-center space-x-2 ml-4">
                      <select
                        value={feedback.status}
                        onChange={(e) => handleStatusChange(feedback.id, e.target.value as any)}
                        className="text-sm px-2 py-1 border border-gray-300 rounded focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                      >
                        <option value="inviato">Inviato</option>
                        <option value="letto">Letto</option>
                        <option value="risolto">Risolto</option>
                      </select>
                      <button
                        onClick={() => handleDelete(feedback.id)}
                        className="p-2 text-red-600 hover:text-red-800 hover:bg-red-50 rounded transition-colors"
                        title="Elimina feedback"
                      >
                        <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
                        </svg>
                      </button>
                    </div>
                  </div>
                  <div className="bg-gray-50 p-4 rounded-lg">
                    <p className="text-gray-900 whitespace-pre-wrap">
                      {feedback.message}
                    </p>
                  </div>
                </div>
              ))
            )}
          </div>
        )}
      </div>

      {/* Modal di conferma */}
      <ConfirmationModal
        isOpen={confirmModal.isOpen}
        onClose={() => setConfirmModal({ ...confirmModal, isOpen: false })}
        onConfirm={confirmModal.onConfirm}
        title={confirmModal.title}
        message={confirmModal.message}
        action={confirmModal.action}
      />
    </div>
  );
};

export default FeedbackManagement;

import React, { useState, useEffect } from 'react';
import { BetaFeedback } from '../../types';
import * as feedbackService from '../../services/feedbackService';
import { ChevronLeftIcon } from '../icons';

interface FeedbackManagementProps {
  onBack: () => void;
}

// Modal di conferma per eliminazione
interface ConfirmationModalProps {
  isOpen: boolean;
  onClose: () => void;
  onConfirm: () => void;
  title: string;
  message: string;
  action: string;
}

const ConfirmationModal: React.FC<ConfirmationModalProps> = ({
  isOpen, onClose, onConfirm, title, message, action
}) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg max-w-md w-full p-6">
        <h3 className="text-lg font-semibold text-gray-900 mb-2">{title}</h3>
        <p className="text-gray-600 mb-6">{message}</p>
        <div className="flex justify-end space-x-3">
          <button
            onClick={onClose}
            className="px-4 py-2 text-gray-700 bg-gray-100 rounded-md hover:bg-gray-200 transition-colors"
          >
            Annulla
          </button>
          <button
            onClick={() => {
              onConfirm();
              onClose();
            }}
            className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 transition-colors"
          >
            {action}
          </button>
        </div>
      </div>
    </div>
  );
};

const FeedbackManagement: React.FC<FeedbackManagementProps> = ({ onBack }) => {
  const [feedbacks, setFeedbacks] = useState<BetaFeedback[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');

  // Filtri
  const [selectedSection, setSelectedSection] = useState('');
