import {
  collection,
  addDoc,
  query,
  orderBy,
  where,
  getDocs,
  Timestamp,
  doc,
  updateDoc,
  deleteDoc
} from 'firebase/firestore';
import { db } from './firebase';
import { BetaFeedback } from '../types';

const FEEDBACK_COLLECTION = 'feedback_tester';

/**
 * Crea un nuovo feedback
 */
export const createFeedback = async (
  userId: string,
  userEmail: string,
  userName: string,
  section: string,
  message: string
): Promise<void> => {
  try {
    await addDoc(collection(db, FEEDBACK_COLLECTION), {
      userId,
      userEmail,
      userName,
      section,
      message,
      status: 'inviato',
      createdAt: Timestamp.now(),
      updatedAt: Timestamp.now()
    });
  } catch (error) {
    console.error('Errore nella creazione del feedback:', error);
    throw new Error('Impossibile salvare il feedback');
  }
};

/**
 * Aggiorna lo stato di un feedback
 */
export const updateFeedbackStatus = async (
  feedbackId: string,
  status: 'inviato' | 'letto' | 'risolto'
): Promise<void> => {
  try {
    const feedbackRef = doc(db, FEEDBACK_COLLECTION, feedbackId);
    await updateDoc(feedbackRef, {
      status,
      updatedAt: Timestamp.now()
    });
  } catch (error) {
    console.error('Errore nell\'aggiornamento del feedback:', error);
    throw new Error('Impossibile aggiornare il feedback');
  }
};

/**
 * Elimina un feedback
 */
export const deleteFeedback = async (feedbackId: string): Promise<void> => {
  try {
    const feedbackRef = doc(db, FEEDBACK_COLLECTION, feedbackId);
    await deleteDoc(feedbackRef);
  } catch (error) {
    console.error('Errore nell\'eliminazione del feedback:', error);
    throw new Error('Impossibile eliminare il feedback');
  }
};

/**
 * Recupera tutti i feedback con filtri opzionali
 */
export const fetchFeedbacks = async (filters?: {
  section?: string;
  userId?: string;
  status?: string;
  startDate?: Date;
  endDate?: Date;
}): Promise<BetaFeedback[]> => {
  try {
    let q = query(collection(db, FEEDBACK_COLLECTION), orderBy('createdAt', 'desc'));

    if (filters?.section) {
      q = query(q, where('section', '==', filters.section));
    }

    if (filters?.userId) {
      q = query(q, where('userId', '==', filters.userId));
    }

    if (filters?.status) {
      q = query(q, where('status', '==', filters.status));
    }

    if (filters?.startDate) {
      q = query(q, where('createdAt', '>=', Timestamp.fromDate(filters.startDate)));
    }

    if (filters?.endDate) {
      q = query(q, where('createdAt', '<=', Timestamp.fromDate(filters.endDate)));
    }

    const snapshot = await getDocs(q);
    return snapshot.docs.map(doc => {
      const data = doc.data();
      return {
        id: doc.id,
        userId: data.userId,
        userEmail: data.userEmail,
        userName: data.userName,
        section: data.section,
        message: data.message,
        status: data.status,
        createdAt: data.createdAt.toDate(),
        updatedAt: data.updatedAt?.toDate()
      };
    });
  } catch (error) {
    console.error('Errore nel recupero dei feedback:', error);
    throw new Error('Impossibile recuperare i feedback');
  }
};

/**
 * Recupera le sezioni uniche dai feedback per i filtri
 */
export const getUniqueSections = async (): Promise<string[]> => {
  try {
    const snapshot = await getDocs(collection(db, FEEDBACK_COLLECTION));
    const sections = new Set<string>();

    snapshot.docs.forEach(doc => {
      const data = doc.data();
      if (data.section) {
        sections.add(data.section);
      }
    });

    return Array.from(sections).sort();
  } catch (error) {
    console.error('Errore nel recupero delle sezioni:', error);
    return [];
  }
};

/**
 * Recupera gli utenti unici che hanno inviato feedback
 */
export const getUniqueUsers = async (): Promise<Array<{userId: string, userEmail: string}>> => {
  try {
    const snapshot = await getDocs(collection(db, FEEDBACK_COLLECTION));
    const users = new Map<string, string>();

    snapshot.docs.forEach(doc => {
      const data = doc.data();
      if (data.userId && data.userEmail) {
        users.set(data.userId, data.userEmail);
      }
    });

    return Array.from(users.entries()).map(([userId, userEmail]) => ({
      userId,
      userEmail
    })).sort((a, b) => a.userEmail.localeCompare(b.userEmail));
  } catch (error) {
    console.error('Errore nel recupero degli utenti:', error);
    return [];
  }
};

import { UserRole, UserPermissions } from '../types';

/**
 * Restituisce i permessi di default per ogni ruolo
 */
export const getDefaultPermissions = (role: UserRole): UserPermissions => {
  switch (role) {
    case UserRole.ADMIN:
      return {
        canCreateGems: true,
        canEditGems: true,
        canDeleteGems: true,
        canManageUsers: true,
        canModerateContent: true,

import { UserRole, UserPermissions } from '../types.js';

/**
 * Restituisce i permessi di default per ogni ruolo
 */
export const getDefaultPermissions = (role: UserRole): UserPermissions => {
  switch (role) {
    case UserRole.ADMIN:
      return {
        canCreateGems: true,
        canEditGems: true,
        canDeleteGems: true,
        canManageUsers: true,
        canModerateContent: true,
        canViewDashboard: true,
      };

    case UserRole.MODERATOR:
      return {
        canCreateGems: true,
        canEditGems: true,
        canDeleteGems: false,
        canManageUsers: false,
        canModerateContent: true,
        canViewDashboard: true,
      };

    case UserRole.BETATESTER:
      return {
        canCreateGems: false,
        canEditGems: false,
        canDeleteGems: false,
        canManageUsers: false,
        canModerateContent: false,
        canViewDashboard: false,
      };

    case UserRole.USER:
    default:
      return {
        canCreateGems: false,
        canEditGems: false,
        canDeleteGems: false,
        canManageUsers: false,
        canModerateContent: false,
        canViewDashboard: false,
      };
  }
};

/**
 * Verifica se un utente ha un permesso specifico
 */
export const hasPermission = (
  userPermissions: UserPermissions,
  permission: keyof UserPermissions
): boolean => {
  return userPermissions[permission] === true;
};

/**
 * Verifica se un utente ha almeno uno dei ruoli specificati
 */
export const hasRole = (userRole: UserRole, allowedRoles: UserRole[]): boolean => {
  return allowedRoles.includes(userRole);
};

/**
 * Verifica se un utente è admin
 */
export const isAdmin = (userRole: UserRole): boolean => {
  return userRole === UserRole.ADMIN;
};

/**
 * Verifica se un utente è moderatore o admin
 */
export const isModerator = (userRole: UserRole): boolean => {
  return userRole === UserRole.MODERATOR || userRole === UserRole.ADMIN;
};

/**
 * Hook personalizzato per verificare i permessi dell'utente corrente
 */
export const useUserPermissions = (user: { role: UserRole; permissions: UserPermissions } | null) => {
  if (!user) {
    return {
      canCreateGems: false,
      canEditGems: false,
      canDeleteGems: false,
      canManageUsers: false,
      canModerateContent: false,
      isAdmin: false,
      isModerator: false,
    };
  }

  return {
    ...user.permissions,
    isAdmin: isAdmin(user.role),
    isModerator: isModerator(user.role),
  };
};

export enum Topic {
  GENERAL_CULTURE = "Cultura Generale & Curiosità",
  SCIENCE_TECH = "Scienza, Tecnologia & Futuro",
  ART_DESIGN = "Arte, Design & Creatività",
  WELLBEING = "Benessere & Sviluppo Personale",
  ECONOMY = "Economia, Finanza & Società",
}

export enum UserRole {
  USER = "user",
  MODERATOR = "moderator",
  ADMIN = "admin",
  BETATESTER = "betatester"
}

export interface UserPermissions {
  canCreateGems: boolean;
  canEditGems: boolean;
  canDeleteGems: boolean;
  canManageUsers: boolean;
  canModerateContent: boolean;
  canViewDashboard: boolean;
}

export interface UserQuestion {
  id: string;
  question: string;
  answer: string;
  isGenerating?: boolean;
}

export interface Source {
    uri: string;
    title: string;
}

export interface Gem {
  id:string;
  topic: Topic;
  title: string;
  description: string;
  imageUrl: string;
  userQuestions: UserQuestion[];
  tags: string[];
  suggestedQuestions: string[];
  sources: Source[];
}

export interface Channel {
    id: string;
    name: string;
    description: string;
    createdAt: Date;
    isActive: boolean;
}

export interface SavedList {
    id: string;
    name: string;
    gemIds: string[];
}

// Nuovi tipi per la struttura ristrutturata
export interface List {
    id: string;
    name: string;
    description?: string;
    isPublic: boolean;
    createdBy: string; // userId
    createdAt: Date;
    updatedAt: Date;
    color?: string; // Per personalizzazione UI futura
    icon?: string; // Emoji o icona
}

export interface ListItem {
    id: string;
    listId: string;
    gemId: string;
    addedBy: string; // userId
    addedAt: Date;
    order?: number; // Per ordinamento personalizzato
}

export interface ListMember {
    id: string;
    listId: string;
    userId: string;
    role: 'owner' | 'editor' | 'viewer';
    joinedAt: Date;
}

// Tipo per le viste aggregate (per compatibilità con UI esistente)
export interface ListWithItems {
    id: string;
    name: string;
    description?: string;
    isPublic: boolean;
    createdBy: string;
    createdAt: Date;
    updatedAt: Date;
    color?: string;
    icon?: string;
    gemIds: string[];
    itemCount: number;
    userRole?: 'owner' | 'editor' | 'viewer';
}

export type Filter =
  | { type: 'all' }
  | { type: 'favorites' }
  | { type: 'topic', value: Topic }
  | { type: 'channel', value: string }
  | { type: 'tag', value: string };

export interface TopicSuggestion {
  id: string;
  text: string;
  tags: string[];
  createdAt: Date;
  updatedAt: Date;
  createdBy: string; // UID dell'utente che ha creato l'argomento
  status: 'pending' | 'approved' | 'converted'; // Stato dell'argomento
}

export interface BetaFeedback {
  id: string;
  userId: string;
  userEmail: string;

